// React
import React, { Fragment, useEffect, useState } from "react";

// Third Part
import { Layout } from "antd";

// Custom
import {
  HeaderTitle,
  CustomTable,
  CustomModal,
  openNotification,
  CustomDrawer,
  CustomButton
} from "../common";
// Services
import {
  getDishes,
  addDishes,
  deleteDishes,
  updateDishes
} from "../../services/dishServices";

const { Content } = Layout;

const CustomContent = () => {
  // initializing states
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState();
  const [editData, setEditData] = useState({});
  const [drawerVisible, setDrawerVisible] = useState();

  //   hook cycles
  const fetchDishData = async () => {
    await getDishes()
      .then(res => res.data)
      .then(res => setData(res))
      .catch(err =>
        openNotification(
          "error",
          "Cannot get informations",
          "Please check connection"
        )
      );
  };
  useEffect(() => {
    fetchDishData();
  }, []);

  // custom Drawer
  const showDrawer = () => {
    setDrawerVisible(true);
  };

  const onClose = () => {
    setDrawerVisible(false);
  };

  //   Modal handle
  const onCancel = () => {
    setVisible(false);
  };

  //   handle actions in table item
  const onhandleUpdate = async item => {
    const orginaldata = { ...item };
    try {
      await updateDishes(item);
    } catch (error) {
      setEditData(orginaldata);
    }
  };
  const deleteItem = async item => {
    const orginaldata = data;
    try {
      await deleteDishes(item);
    } catch (error) {
      setData(orginaldata);
    }
  };

  const editItem = async item => {
    const newData = { ...item };
    setEditData(newData);
    setVisible(true);
  };

  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Dish Menu"} />
          <CustomButton title="Add" showDrawer={showDrawer} />

          <CustomDrawer visible={drawerVisible} onClose={onClose} />
          <CustomTable
            data={data}
            colOne={"name"}
            colTwo={"price"}
            colThird={"type"}
            colFourth={"date"}
            colFive={"Actions"}
            deleteItem={deleteItem}
            editItem={editItem}
          />
          <CustomModal
            visible={visible}
            onCancel={onCancel}
            data={editData}
            onhandleUpdate={onhandleUpdate}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default CustomContent;
