import React, { Component, Fragment } from "react";
import {
  Layout,
  Table,
  Button,
  InputNumber,
  Input,
  Form,
  Row,
  Col
} from "antd";
import { Resizable } from "react-resizable";

import "../../../assets/css/AdminForm.css";
import WrappedRegistrationForm from "../../common/dishDrawer";
import Title from "../../common/title";

// for resizing
const ResizeableTitle = props => {
  const { onResize, width, ...restProps } = props;

  if (!width) {
    return <th {...restProps} />;
  }

  return (
    <Resizable
      width={width}
      height={0}
      onResize={onResize}
      draggableOpts={{ enableUserSelectHack: false }}
    >
      <th {...restProps} />
    </Resizable>
  );
};

// for editable
const EditableContext = React.createContext();

class EditableCell extends Component {
  getInput = () => {
    if (this.props.inputType === "number") {
      return <InputNumber />;
    }
    return <Input />;
  };

  renderCell = ({ getFieldDecorator }) => {
    const {
      editing,
      dataIndex,
      title,
      inputType,
      record,
      index,
      children,
      ...restProps
    } = this.props;
    return (
      <td {...restProps}>
        {editing ? (
          <Form.Item style={{ margin: 0 }}>
            {getFieldDecorator(dataIndex, {
              rules: [
                {
                  required: true,
                  message: `Please Input ${title}!`
                }
              ],
              initialValue: record[dataIndex]
            })(this.getInput())}
          </Form.Item>
        ) : (
          children
        )}
      </td>
    );
  };

  render() {
    return (
      <EditableContext.Consumer>{this.renderCell}</EditableContext.Consumer>
    );
  }
}

const { Content } = Layout;
class Dish extends Component {
  state = {
    columns: [
      {
        title: "Name",
        dataIndex: "name",
        editable: true
      },
      {
        title: "Price",
        dataIndex: "price",
        editable: true
      },
      {
        title: "Type",
        dataIndex: "type",
        editable: true
      },
      {
        title: "Action",
        dataIndex: "operation",
        render: (text, record) => {
          const { editingKey } = this.state;
          const editable = this.isEditing(record);
          return editable ? (
            <span>
              <EditableContext.Consumer>
                {form => (
                  <Row gutter={22}>
                    <Col span={5}>
                      <Button
                        size="small"
                        onClick={() => this.save(form, record.key)}
                        style={{ width: "60px" }}
                      >
                        Save
                      </Button>
                    </Col>
                    <Col span={5}>
                      <Button
                        size="small"
                        style={{ width: "70px" }}
                        onClick={() => this.cancel(record.key)}
                      >
                        Cancel
                      </Button>
                    </Col>
                  </Row>
                )}
              </EditableContext.Consumer>
            </span>
          ) : (
            <Fragment>
              <Button
                type="danger"
                size="small"
                style={{ width: "60px", margin: " 0 1em" }}
                onClick={() => this.handleDelete(record.key)}
              >
                Delete
              </Button>
              <Button
                type="primary"
                size="small"
                style={{ width: "60px" }}
                disabled={editingKey !== ""}
                onClick={() => this.edit(record.key)}
              >
                Edit
              </Button>
            </Fragment>
          );
        }
      }
    ],

    visible: false,
    dataSource: [],
    key: 0,
    editingKey: "",
    name: "",
    price: "",
    type: "",
    title: "Dish Menu"
  };

  namehandleChange = event => {
    this.setState({
      name: event.target.value
    });
  };
  priceHandleChange = price => {
    this.setState({
      price: price
    });
  };

  typehandleChange = type => {
    this.setState({
      type: type
    });
  };

  isEditing = record => record.key === this.state.editingKey;

  cancel = () => {
    this.setState({ editingKey: "" });
  };
  edit(key) {
    this.setState({ editingKey: key });
  }

  handleDelete = key => {
    const dataSource = [...this.state.dataSource];
    this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
  };

  save(form, key) {
    form.validateFields((error, row) => {
      if (error) {
        return;
      }
      const newData = [...this.state.dataSource];
      const index = newData.findIndex(item => key === item.key);
      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row
        });
        this.setState({ dataSource: newData, editingKey: "" });
      } else {
        newData.push(row);
        this.setState({ dataSource: newData, editingKey: "" });
      }
    });
  }
  components = {
    header: {
      cell: ResizeableTitle
    },
    body: {
      cell: EditableCell
    }
  };
  // form item
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      console.log(values);
    });
  };

  handleAdd = () => {
    const { key, dataSource, name, price, type } = this.state;
    const newData = {
      key: key,
      name: name,
      price: price,
      type: type
    };
    if (name !== "" && price !== null && type !== "") {
      this.setState({
        dataSource: [...dataSource, newData],
        key: key + 1
      });
    }
  };
  showDrawer = () => {
    this.setState({
      visible: true
    });
  };
  onClose = () => {
    this.setState({
      visible: false
    });
  };
  handleResize = index => (e, { size }) => {
    this.setState(({ columns }) => {
      const nextColumns = [...columns];
      nextColumns[index] = {
        ...nextColumns[index],
        width: size.width
      };
      return { columns: nextColumns };
    });
  };

  render() {
    const columns = this.state.columns.map((col, index) => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onHeaderCell: column => ({
          width: column.width,
          onResize: this.handleResize(index)
        }),
        onCell: record => ({
          record,
          inputType: col.dataIndex === "age" ? "number" : "text",
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record)
        })
      };
    });
    const { dataSource, visible, title } = this.state;
    return (
      <Fragment>
        <Content style={{ margin: "2em 16px" }}>
          <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
            <Title title={title} />
            <div style={{ textAlign: "right" }}>
              <Button
                type="primary"
                onClick={this.showDrawer}
                style={{ margin: "2em" }}
              >
                Add
              </Button>
            </div>
            <WrappedRegistrationForm
              onClose={this.onClose}
              visible={visible}
              name={this.state.name}
              form={this.props.form}
              price={this.state.price}
              handleAdd={this.handleAdd}
              handleSubmit={this.handleSubmit}
              namehandleChange={this.namehandleChange}
              priceHandleChange={this.priceHandleChange}
              typehandleChange={this.typehandleChange}
            />
            <EditableContext.Provider value={this.props.form}>
              <Table
                components={this.components}
                columns={columns}
                dataSource={dataSource}
                bordered
                rowClassName="editable-row"
                title={() => "Dish Menu"}
                footer={() => "Footer"}
                pagination={false}
                scroll={{ y: 500 }}
              />
            </EditableContext.Provider>
          </div>
        </Content>
      </Fragment>
    );
  }
}

const WrappedHorizontalLoginForm = Form.create()(Dish);
export default WrappedHorizontalLoginForm;
