import React, { Fragment, Component } from "react";

import { Card, Button, Input, Row, Col } from "antd";

const { Meta } = Card;

export default class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: this.props.location.state.name,
      order: this.props.location.state.order
    };
  }
  nameChange = value => {
    this.setState({ name: value.target.value });
  };

  orderChange = order => {
    this.setState({ order: order.target.value });
  };

  saveHandle = () => {
    console.log("saved");
  };

  render() {
    //   props.location.state.name
    //   props.location.state.order

    return (
      <Fragment>
        <div
          style={{
            background: "#fff",
            padding: 24,
            minHeight: 680
          }}
        >
          <Card
            title={this.props.location.state.title}
            cover={<img alt="" src={this.props.location.state.url} />}
            style={{ width: "400px" }}
          >
            <Meta
              title={
                <Input value={this.state.name} onChange={this.nameChange} />
              }
              description={
                <Input value={this.state.order} onChange={this.orderChange} />
              }
            />
          </Card>
          <Row>
            <Col span={4}>
              <Button
                style={{ width: "150px", margin: "1em 0" }}
                onClick={this.saveHandle}
              >
                Save
              </Button>
            </Col>
            <Col span={4}>
              <Button
                style={{ width: "150px", margin: "1em 0" }}
                onClick={() => this.props.history.push("/order")}
              >
                Back to Order
              </Button>
            </Col>
          </Row>
        </div>
      </Fragment>
    );
  }
}
