import React, { Component } from "react";
import { Button, Icon, Divider } from "antd";
import ReservationDrawer from "../../common/reservationDrawer";
import ReservationList from "../../common/reservationList";
import CustomModal from "../../common/customModal";
export default class Reservation extends Component {
  state = {
    data: [
      {
        id: 1,
        usernameValue: "Alex",
        tableNumber: "1",
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        seats: "4",
        date: "2020-01-01 08:47:47"
      },
      {
        id: 2,
        usernameValue: "Mike",
        tableNumber: "2",
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        seats: "6",
        date: "2020-01-01 05:47:47"
      }
    ],
    visible: false,
    modal_visible: false,
    id: 0,
    usernameValue: "",
    tableNumber: "",
    url: "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
    seats: "",
    date: "",
    editableData: {}
  };

  showDrawable = () => {
    this.setState({ visible: true });
  };

  onClose = () => {
    this.setState({ visible: false });
  };

  onNameChange = event => {
    this.setState({ usernameValue: event.target.value });
  };
  onNumberChange = number => {
    this.setState({ tableNumber: number });
  };

  onHandleAdd = () => {
    const {
      data,
      id,
      usernameValue,
      tableNumber,
      url,
      seats,
      date
    } = this.state;
    const newData = {
      id: id,
      tableNumber: tableNumber,
      usernameValue: usernameValue,
      url: url,
      seats: seats,
      date: date
    };
    this.setState({
      data: [...data, newData],
      id: id + 1
    });
  };
  onDataChange = (value, dateString) => {
    console.log(dateString);
    this.setState({ date: dateString });
  };
  onSeatsChange = value => {
    this.setState({ seats: value });
  };

  onDelete = itemId => {
    const newData = this.state.data.filter(item => item.id !== itemId);
    this.setState({ data: newData });
  };

  onEdit = item => {
    const newDate = { ...item };
    this.setState({ modal_visible: true, editableData: newDate });
  };

  handleOk = e => {
    let newData = { ...this.state.data, e };
    this.setState({ modal_visible: false, editableData: newData });
  };
  onCancel = () => {
    this.setState({ modal_visible: false });
  };

  handleTableNumberChange = tableNumber => {
    let data = { ...this.state.editableData, tableNumber };
    this.setState({ editableData: data });
  };

  handleCustomerChange = customerName => {
    let data = this.state.editableData;
    data.usernameValue = customerName.target.value;
    const newData = { ...data, customerName };
    this.setState({ editableData: newData });
    console.log(newData);
  };

  handleSeatsChange = orderedSeats => {
    let data = { ...this.state.editableData, orderedSeats };
    this.setState({ editableData: data });
  };
  handleDateChange = dateChange => {
    let data = this.state.editableData;
    data.date = dateChange.target.value;
    const newData = { ...data, dateChange };
    this.setState({ editableData: newData });
  };

  render() {
    const {
      data,
      visible,
      usernameValue,
      tableNumber,
      seats,
      modal_visible,
      editableData
    } = this.state;
    return (
      <div style={{ background: "#fff", padding: 24, minHeight: 680 }}>
        <div style={{ textAlign: "right" }}>
          <Button
            type="primary"
            style={{ width: "150px" }}
            onClick={this.showDrawable}
          >
            <Icon type="plus" />
            New Reserv
          </Button>
        </div>
        <Divider />

        {/* ReserVation list */}

        <ReservationList
          data={data}
          onDelete={this.onDelete}
          onEdit={this.onEdit}
        />
        {/* Modal */}
        <CustomModal
          visible={modal_visible}
          handleOk={this.handleOk}
          handleClose={this.onCancel}
          editableData={editableData}
          handleTableNumberChange={this.handleTableNumberChange}
          handleCustomerChange={this.handleCustomerChange}
          handleSeatsChange={this.handleSeatsChange}
          handleDateChange={this.handleDateChange}
        />

        {/* Adding new Item */}
        <ReservationDrawer
          visible={visible}
          userName={usernameValue}
          tableNumber={tableNumber}
          seats={seats}
          onNameChange={this.onNameChange}
          onNumberChange={this.onNumberChange}
          onClose={this.onClose}
          onhandleAdd={this.onHandleAdd}
          onDataChange={this.onDataChange}
          onSeatsChange={this.onSeatsChange}
        />
      </div>
    );
  }
}
