import React, { useState, useEffect } from "react";
import _ from "lodash";
import {
  Layout,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Divider,
  Avatar,
} from "antd";
import { getUser } from "../../../../services/userServices";
import { name } from "../../../../config.json";
import { openNotification } from "../../../custom";

const Profile = () => {
  const [user, setUser] = useState([]);
  const [currentUser] = useState(localStorage.getItem(name));

  useEffect(() => {
    const fetchUser = async () => {
      try {
        await getUser()
          .then((res) => res.data)
          .then((data) => setUser(_.find(data, ["last_name", currentUser])));
      } catch (error) {
        openNotification("error", "Can't get Orders", "Check Connectin");
      }
    };
    fetchUser();
  }, [currentUser]);
  return (
    <Layout.Content style={{ margin: "2em 16px" }}>
      <div
        style={{ background: "#fff", padding: 24, minHeight: 280 }}
        className="border border-dark rounded"
      >
        <div style={{ display: "flex" }}>
          <Avatar
            style={{ backgroundPosition: "center" }}
            src="https://images.unsplash.com/photo-1485875437342-9b39470b3d95?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=800&q=60"
            size={200}
            alt="User image"
          />
          <div>
            <h3>
              {user.first_name}+""+{user.last_name}
            </h3>
          </div>

          {/* <h3>{`Gender: ${user.gender}`}</h3>
              <h3>{`Age: ${user.age}`}</h3> */}
          {/* <h3>{`Address: ${user.address}`}</h3> */}
          {/* <h3>{`Email: ${user.email}`}</h3> */}
          {/* <h3>
                <div
                  style={{ overflowX: "hidden" }}
                >{`Password: ${user.password}`}</div>
              </h3> */}
          {/* <h3>{`Phone number: ${user.phone_number}`}</h3>
              <h3>{`Work position: ${user.work_position}`}</h3>
              <h3>{`Salary: ${user.salary}¥`}</h3> */}
        </div>

        {/* <Col span={8}>
            <Form></Form>
          </Col> */}
      </div>
    </Layout.Content>
  );
};

export default Profile;
