// react
import React, { useState, Fragment } from "react";

// third part
import { Layout, Menu, Icon } from "antd";
import { HashRouter as Router, Route, Link } from "react-router-dom";

// components
import { Dish, Personal, Table } from "./components/";
import Footer from "../common/footer";

// custom
import "../../assets/css/AdminForm.css";

// Icons
import { Breakfast, PersonalIcon, TableInfo } from "../../assets/";

const { Header, Content, Sider } = Layout;

const AdminBoard = () => {
  const [collapsed, setCollapsed] = useState(false);

  toggle = () => {
    // this.setState({
    //   collapsed: !this.state.collapsed
    // });
    setCollapsed((collapsed = true));
  };

  const { history } = this.props;
  return (
    <Fragment>
      <Router>
        <Layout style={{ minHeight: "100vh" }}>
          <Sider trigger={null} collapsible collapsed={collapsed}>
            <div className="logo">
              <p style={{ textAlign: "center", fontSize: "20px" }}>Admin</p>
            </div>

            <Menu theme="light" mode="inline">
              <Menu.Item key="1">
                <Breakfast />
                <span className="menu-title">Dish information</span>
                <Link to="/dish" />
              </Menu.Item>
              <Menu.Item key="2">
                <PersonalIcon />
                <span className="menu-title">Personal Information</span>
                <Link to="/personal" />
              </Menu.Item>

              <Menu.Item key="3">
                <TableInfo />
                <span className="menu-title">Table Information</span>
                <Link to="/table" />
              </Menu.Item>
              <Menu.Item>
                <Icon
                  type="logout"
                  style={{ fontSize: "30px", marginTop: "5px" }}
                  onClick={() => history.replace("/")}
                />
                <span
                  className="menu-title"
                  onClick={() => history.replace("/")}
                >
                  Logout
                </span>
              </Menu.Item>
            </Menu>
          </Sider>
          <Layout>
            <Header style={{ background: "#000000", padding: 0 }}>
              <Icon
                className="trigger"
                type={collapsed ? "menu-unfold" : "menu-fold"}
                onClick={(setCollapsed = false)}
              />
            </Header>
            <Content style={{ margin: "2em 16px" }}>
              <Route exact path="/dish" component={Dish} />
              <Route exact path="/personal" component={Personal} />
              <Route exact path="/table" component={Table} />
            </Content>
            <Footer />
          </Layout>
        </Layout>
      </Router>
    </Fragment>
  );
};

export default AdminBoard;
