// React
import React, { useState, useEffect } from "react";

// Third Part
import {
  Row,
  Col,
  Drawer,
  Form,
  Select,
  Input,
  DatePicker,
  Button
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { openNotification } from "../../../../custom";

const { RangePicker } = DatePicker;

const ReservationDrawer = ({ visible, onClose, onHandleAdd }) => {
  const [tableNumber, setTableNumber] = useState([]);
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  useEffect(() => {
    fetchTableNumber();
  }, []);

  return (
    <Drawer
      title="Reserv place"
      visible={visible}
      closable={false}
      placement="bottom"
      onClose={onClose}
    >
      <Form hideRequiredMark onFinish={onHandleAdd}>
        <Row>
          <Col span={5}>
            <Form.Item
              label="Table_number"
              name="table_number"
              rules={[{ required: true, message: "Enter tabel Number" }]}
            >
              <Select placeholder="Select Table Number">
                {tableNumber.map(table => (
                  <Select.Option key={table.id} value={table.table_number}>
                    {table.table_number}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={5} offset={1}>
            <Form.Item
              label="Customer Name"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer name" }]}
            >
              <Input placeholder="Enter Customer Name" />
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item
              label="seats"
              name="seats"
              rules={[{ required: true, message: "Enter Seats Number" }]}
            >
              <InputNumber min={0} />
            </Form.Item>
          </Col>
          <Col offset={1}>
            <Form.Item
              label="Date"
              name="date"
              rules={[{ required: true, message: "Choose the Date" }]}
            >
              <RangePicker showTime placeholder="Select the Date" />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button htmlType="submit" type="primary">
              add
            </Button>
          </Col>
          <Col>
            <Button className="ml-2" type="danger" onClick={onClose}>
              close
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default ReservationDrawer;
