//React
import React, { Fragment, useState, useEffect } from "react";

// Third part
import { Divider, Input, Card, InputNumber } from "antd";
import { SearchOutlined } from "@ant-design/icons";

// Tools
import _ from "lodash";

// Services
import { getOrder } from "../../../../services/orderServices";

// custom Components
import { openNotification } from "../../../custom/";

const Search = () => {
  // state
  const [orderData, setOrderData] = useState([]);
  const [searchData, setSearchData] = useState({});
  // searchMethod
  const SearchOrders = value => {
    const table = _.find(orderData, ["table", value]);
    const newData = { ...table };
    setSearchData(newData);
  };
  // fetch dataMethod
  const getData = async () => {
    try {
      await getOrder()
        .then(res => res.data)
        .then(data => setOrderData(data));
    } catch (err) {
      openNotification("error", "Can't get data", "Check connection");
    }
  };
  // Hook
  useEffect(() => {
    getData();
  }, []);

  return (
    <Fragment>
      <InputNumber
        min={0}
        placeholder="Input TableNumber"
        className="w-100"
        onChange={value => SearchOrders(value)}
      />
      <Divider />
      <Card title={searchData.table} cover={searchData.customer}></Card>
    </Fragment>
  );
};

export default Search;
