// React
import React, { useState } from "react";

// third part
import { Layout, Divider } from "antd";

// custom
import { HeaderTitle, CustomDrawerButton } from "../../../../custom/";
import * as All from "./";
const { Content } = Layout;

const ReservationContent = () => {
  const [DrawerVisible, setDrawerVisible] = useState();
  const showDrawer = () => {
    setDrawerVisible(true);
  };
  const onClose = () => {
    setDrawerVisible(false);
    window.location.reload();
  };

  const onHandleAdd = async items => {
    console.log(items);
  };
  return (
    <Content style={{ margin: "2em 16px" }}>
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <HeaderTitle title={"Reservation Menu"} />
        <CustomDrawerButton title={"Reserv place"} showDrawer={showDrawer} />
        <Divider />
        <All.ReservationDrawer
          visible={DrawerVisible}
          onHandleAdd={onHandleAdd}
          onClose={onClose}
        />
      </div>
    </Content>
  );
};

export default ReservationContent;
