// React
import React, { useState, useEffect } from "react";

// third part
import { Layout, Divider } from "antd";

// custom
import {
  HeaderTitle,
  CustomDrawerButton,
  openNotification
} from "../../../../custom/";
import * as All from "./";

// Services

import {
  getReservation,
  addReservation
} from "../../../../../services/reservationServices";

const { Content } = Layout;

const ReservationContent = () => {
  const [DrawerVisible, setDrawerVisible] = useState();
  const [getReservationData, setReservationData] = useState([]);

  const fetchReservationData = async () => {
    await getReservation().then(res => console.log(res));
  };

  useEffect(() => {
    fetchReservationData();
  }, []);

  const showDrawer = () => {
    setDrawerVisible(true);
  };

  const onClose = () => {
    setDrawerVisible(false);
    window.location.reload();
  };

  const onHandleAdd = async items => {
    try {
      await addReservation(items);
    } catch (error) {
      openNotification("error", "Can't add user", "Check Connection");
    }
  };
  return (
    <Content style={{ margin: "2em 16px" }}>
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <HeaderTitle title={"Reservation Menu"} />
        <CustomDrawerButton title={"Reserv place"} showDrawer={showDrawer} />
        <Divider />
        <All.ReservationDrawer
          visible={DrawerVisible}
          onHandleAdd={onHandleAdd}
          onClose={onClose}
        />
        <All.ReservationList />
      </div>
    </Content>
  );
};

export default ReservationContent;
