import React from "react";
import {
  Drawer,
  Input,
  InputNumber,
  Select,
  Button,
  Form,
  Row,
  Col
} from "antd";
import FormItem from "antd/lib/form/FormItem";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange
}) => {
  return (
    <Drawer
      width={420}
      title="Basic Drawer"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form>
        <Form.Item>
          <Input
            placeholder="name"
            value={name}
            onChange={namehandleChange}
            allowClear
          />
        </Form.Item>
        <Form.Item>
          <InputNumber
            placeholder="price"
            value={price}
            onChange={priceHandleChange}
            style={{ width: "380px" }}
          />
        </Form.Item>

        <Form.Item>
          <Select onChange={typehandleChange} defaultValue="select type">
            <Option value="snackðŸ¿">Snack</Option>
            <Option value="soupðŸœ">Soup</Option>
            <Option value="desertðŸ®">Desert</Option>
          </Select>
        </Form.Item>
        <Row>
          <Col span={8}>
            <FormItem>
              <Button onClick={onClose} type="danger">
                Cancel
              </Button>
            </FormItem>
          </Col>
          <Col span={8}>
            <Form.Item>
              <Button onClick={handleAdd} type="primary">
                Add
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default Drawers;
