// React
import React, { useState, useEffect } from "react";

// third part
import {
  Drawer,
  Form,
  Select,
  Input,
  TimePicker,
  Button,
  TreeSelect,
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";

// custom
import { openNotification } from "../../../../custom";

const { TreeNode } = TreeSelect;

const layout = {
  labelCol: { span: 5 },
  wrapperCol: { span: 24 },
};

const OrderDrawer = ({ visible, onClose, handleAdd }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then((res) => res.data)
        .then((data) => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then((res) => res.data)
        .then((data) => setOrders(data));
    } catch (error) {}
  };

  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
      width={480}
    >
      <Form {...layout} hideRequiredMark onFinish={handleAdd}>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="select table number">
            {tableNumber.map((table) => (
              <Select.Option key={table.id} value={table.table_number}>
                {table.table_number}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item
          label="Customer"
          name="customer_name"
          rules={[{ required: true, message: "Enter Customer Name" }]}
        >
          <Input placeholder="Customer Name" allowClear />
        </Form.Item>

        <Form.Item label="Order" name="dish">
          <TreeSelect
            showSearch
            style={{ width: "100%" }}
            dropdownStyle={{ maxHeight: 400, overflow: "auto" }}
            placeholder="Please select"
            allowClear
            multiple
          >
            {orders.map((or) => (
              <TreeNode key={or.id} value={or.name} title={or.name} />
            ))}
          </TreeSelect>
        </Form.Item>
        <Form.Item label="Price" name="price">
          <TreeSelect
            showSearch
            style={{ width: "100%" }}
            dropdownStyle={{ maxHeight: 400, overflow: "auto" }}
            placeholder="Please select"
            allowClear
            multiple
          >
            {orders.map((or) => (
              <TreeNode
                key={or.id}
                selectable={false}
                value={or.name}
                title={or.name}
              >
                <TreeNode key={or.id} value={or.price} title={or.price} />
              </TreeNode>
            ))}
          </TreeSelect>
        </Form.Item>

        <Form.Item label="Time" name="time" placeholder="select time">
          <TimePicker className="w-100" />
        </Form.Item>
        <Button type="primary" htmlType="submit">
          Order
        </Button>
        <Button type="danger" className="ml-3" onClick={onClose}>
          Cancel
        </Button>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
