// react
import React, { useState, useEffect, Fragment } from "react";

// third part
import { Layout, Divider } from "antd";

// custom
import {
  CustomDrawerButton,
  HeaderTitle,
  openNotification
} from "../../../../custom/";
import * as All from "./";

// services
import {
  getTable,
  deleteItems,
  editTables,
  addTables
} from "../../../../../services/tableServices";

const { Content } = Layout;

const TableContent = () => {
  const [table, setTable] = useState([]);
  const [visible, setVisible] = useState();
  const [modalVisible, setModalVisible] = useState();
  const [editTable, setEdibtable] = useState({});

  const fetchTable = async () => {
    // get tables
    await getTable()
      .then(res => res.data)
      .then(data => setTable(data))
      .catch(err =>
        openNotification(
          "error",
          "Can't get information",
          "Please check connection"
        )
      );
  };

  useEffect(() => {
    fetchTable();
  }, []);

  //   Drawer
  const showDrawer = () => {
    setVisible(true);
  };

  const onClose = () => {
    setVisible(false);
  };
  const onHandleAdd = async items => {
    try {
      await addTables(items);
    } catch (error) {
      openNotification("error", "Can't add Items", "Check connection");
    }
  };

  const deleteTableItems = async items => {
    try {
      await deleteItems(items);
    } catch (error) {
      openNotification("error", "Can't delete Items", "Check connection");
    }
  };
  const editItems = items => {
    setModalVisible(true);
    setEdibtable(items);
  };
  const handleEdit = async items => {
    await editTables(items);
    setModalVisible(false);
  };

  const onModalClose = () => {
    setModalVisible(false);
    window.location.reload();
  };

  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title="Table Menu" legth={table.length} />
          <CustomDrawerButton title="Add" showDrawer={showDrawer} />
          <All.TableDrawer
            visible={visible}
            onClose={onClose}
            onHandleAdd={onHandleAdd}
          />
          <Divider />
          <All.Table
            data={table}
            deleteTableItems={deleteTableItems}
            editItems={editItems}
          />
          <All.EditModal
            visible={modalVisible}
            data={editTable}
            handleEdit={handleEdit}
            onClose={onModalClose}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default TableContent;
