// React
import React, { Fragment } from "react";

// Third part
import { Layout, Menu, Typography } from "antd";
import {
  HashRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";
import { Link } from "react-router-dom";

// Custom
import { Order, Reservation, Information, SearchOrders } from "./components/";
import { name, user } from "../../config.json";
import { Footer } from "../custom/";

const { Content } = Layout;
const { Header } = Layout;
const { Text } = Typography;

const EmployeeBoard = ({ history }) => {
  const Logout = () => {
    localStorage.removeItem(name);
    localStorage.removeItem(user);
    history.replace("/");
  };
  return (
    <Fragment>
      <Router>
        <Layout style={{ minHeight: "100vh" }}>
          <Header
            style={{
              position: "fixed",
              zIndex: 1,
              width: "100%",
              background: "#000000"
            }}
          >
            <Menu
              theme="dark"
              mode="horizontal"
              style={{ lineHeight: "64px", background: "black" }}
            >
              <Menu.Item disabled>
                <Text
                  className="border border border-warning p-3 rounded"
                  type="warning"
                >
                  {localStorage.getItem(name)}
                </Text>
              </Menu.Item>
              <Menu.Item key="1">
                Order
                <Link to="/order" />
              </Menu.Item>
              <Menu.Item key="/reservation">
                Reservation
                <Link to="/reservation" />
              </Menu.Item>
              <Menu.Item key="3">
                Info
                <Link to="/info" />
              </Menu.Item>
              <Menu.Item key="4" onClick={() => Logout({})}>
                Logout
              </Menu.Item>
            </Menu>
          </Header>

          <Content style={{ padding: "0 50px", marginTop: 94 }}>
            <Switch>
              <Route path="/order" component={Order} />
              <Route path="/reservation" component={Reservation} />
              <Route path="/info" component={Information} />
              <Route path="/searchOrder" component={SearchOrder} />
              <Redirect from="/" to="/order" />
            </Switch>
          </Content>
          <Footer />
        </Layout>
      </Router>
    </Fragment>
  );
};
export default EmployeeBoard;
