// React
import React, { useState, useEffect } from "react";

// third part
import { Drawer, Form, Select, Input } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";

// custom
import { openNotification } from "../../../../custom";

const { Option } = Select;

const OrderDrawer = ({ visible, onClose }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  useEffect(() => {
    fetchTableNumber();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
    >
      <Form hideRequiredMark>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="select table number">
            {tableNumber.map(table => (
              <Option value={table.table_number}>{table.table_number}</Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item
          label="Customer"
          name="customer_name"
          rules={[{ required: true, message: "Enter Customer Name" }]}
        >
          <Input placeholder="Customer_Name" allowClear />
        </Form.Item>
        <Form.Item label="Order" name="ordered_items">
          <Select mode="tags" placeholder="select order"></Select>
        </Form.Item>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
