import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import { Layout, Menu, Typography } from "antd";

const { Header } = Layout;
const { Text } = Typography;

const EmployeeMenu = ({ link, name }) => {
  return (
    <Fragment>
      <Header
        style={{
          position: "fixed",
          zIndex: 1,
          width: "100%",
          background: "#000000",
          display: "flex"
        }}
      >
        <Menu
          theme="dark"
          mode="horizontal"
          style={{ lineHeight: "64px", background: "black" }}
          selectedKeys="1"
        >
          <Menu.Item disabled>
            <Text className="brand" type="warning">
               {name}
            </Text>
          </Menu.Item>
          <Menu.Item key="1">
            Order
            <Link to="/order" />
          </Menu.Item>
          <Menu.Item key="/reservation">
            Reservation
            <Link to="/reservation" />
          </Menu.Item>
          <Menu.Item key="3">
            Info
            <Link to="/info" />
          </Menu.Item>
          <Menu.Item key="4" onClick={link}>
            Logout
          </Menu.Item>
        </Menu>
      </Header>
    </Fragment>
  );
};

export default EmployeeMenu;
