import React, { Fragment } from "react";
import {
  Drawer,
  Form,
  Input,
  InputNumber,
  DatePicker,
  Button,
  Row,
  Col
} from "antd";
const layout = {
  labelCol: { span: 3 },
  wrapperCol: { span: 16 }
};
const tailLayout = {
  wrapperCol: { offset: 8, span: 16 }
};
const DishDarawer = ({ visible, title, onClose, onhandleAdd }) => {
  return (
    <Drawer
      closable={false}
      title={title}
      width={380}
      visible={visible}
      onClose={onClose}
    >
      <Fragment>
        <Form
          {...layout}
          name="basic"
          onFinish={onhandleAdd}
          hideRequiredMark
          className="m-0"
        >
          <Form.Item
            label="name"
            name="name"
            rules={[{ required: true, message: "Please input name " }]}
          >
            <Input allowClear />
          </Form.Item>

          <Form.Item
            label="Price"
            name="price"
            rules={[{ required: true, message: "Please input price" }]}
          >
            <InputNumber min={0} step={0.1} className="w-100" />
          </Form.Item>
          <Form.Item
            label="Type"
            name="type"
            rules={[{ required: true, message: "Please input type" }]}
          >
            <Input allowClear />
          </Form.Item>
          <Form.Item
            label="Date"
            name="date"
            rules={[{ required: true, message: "Please choose Date" }]}
          >
            <DatePicker className="w-100" />
          </Form.Item>

          <Row>
            <Col span={5}>
              <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">
                  Add
                </Button>
              </Form.Item>
            </Col>
            <Col span={5}>
              <Form.Item {...tailLayout}>
                <Button type="danger" onClick={onClose}>
                  Cancel
                </Button>
              </Form.Item>
            </Col>
          </Row>
        </Form>
      </Fragment>
    </Drawer>
  );
};

export default DishDarawer;
