// React
import React, { Component, Fragment } from "react";

// Third part
import {
  Form,
  Icon,
  Input,
  Button,
  Row,
  Col,
  Typography,
  notification
} from "antd";
import { Link } from "react-router-dom";

import https from "../../services/httpService";
import { apiUrl } from "../../config.json";
// custom
import "../../assets/css/LoginForm.css";

const { Title } = Typography;

function hasErrors(fieldsError) {
  return Object.keys(fieldsError).some(field => fieldsError[field]);
}

class SignInForm extends Component {
  componentDidMount() {
    // To disable submit button at the beginning.
    this.props.form.validateFields();
  }

  openNotification = () => {
    notification.open({
      message: "Notification Title",
      description:
        "This is the content of the notification. This is the content of the notification. This is the content of the notification."
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { history } = this.props;

    this.props.form.validateFields((err, values) => {
      if (!err) {
        https
          .post(apiUrl + "user/login", {
            email: values.email,
            password: values.password
          })
          .then(res => {
            if (res.data.status === 200) {
              history.replace("/admin");
            } else if (res.data.status === 302)
              history.replace(`/user/${res.data.data}`);
            if (res.data.status === 401);
          });
      }
    });
  };
  render() {
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const {
      getFieldDecorator,
      getFieldsError,
      getFieldError,
      isFieldTouched
    } = this.props.form;

    // Only show error after a field is touched.
    const usernameError = isFieldTouched("email") && getFieldError("email");
    const passwordError =
      isFieldTouched("password") && getFieldError("password");

    return (
      <Fragment>
        <div>
          <Row gutter={20}>
            <Col xs={10} xl={10} md={10}>
              <div>
                <img
                  alt="restaurant"
                  style={{ width: "100%" }}
                  src="https://images.unsplash.com/photo-1537047902294-62a40c20a6ae?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1275&q=80"
                />
              </div>
            </Col>
            <Col xs={{ span: 10, offset: 2 }} xl={{ span: 10, offset: 3 }}>
              <Form
                {...formItemLayout}
                onSubmit={this.handleSubmit}
                className="log-form"
              >
                <Title>Welcome Restaurant</Title>
                <Form.Item
                  validateStatus={usernameError ? "error" : ""}
                  help={usernameError || ""}
                >
                  {getFieldDecorator("email", {
                    rules: [
                      {
                        type: "email",
                        message: "The input is not valid E-mail!"
                      },
                      {
                        required: true,
                        message: "Please input your E-mail!"
                      }
                    ]
                  })(
                    <Input
                      prefix={
                        <Icon
                          type="user"
                          style={{ color: "rgba(0,0,0,.25)" }}
                        />
                      }
                      placeholder="Email"
                    />
                  )}
                </Form.Item>

                <Form.Item
                  validateStatus={passwordError ? "error" : ""}
                  help={passwordError || ""}
                >
                  {getFieldDecorator("password", {
                    rules: [
                      {
                        required: true,
                        message: "Please input your Password!"
                      }
                    ]
                  })(
                    <Input
                      prefix={
                        <Icon
                          type="lock"
                          style={{ color: "rgba(0,0,0,.25)" }}
                        />
                      }
                      type="password"
                      placeholder="Password"
                    />
                  )}
                </Form.Item>

                <Form.Item>
                  <div style={{ textAlign: "center" }}>
                    <Button
                      type="primary"
                      block
                      htmlType="submit"
                      disabled={hasErrors(getFieldsError())}
                    >
                      Log in
                    </Button>
                  </div>
                </Form.Item>

                <Link to="/register">
                  <p> or Register</p>
                </Link>
              </Form>
            </Col>
          </Row>
        </div>
      </Fragment>
    );
  }
}

const WrapperLoginForm = Form.create({ name: "login" })(SignInForm);
export default WrapperLoginForm;
