import React from "react";
import {
  Drawer,
  Form,
  Input,
  Button,
  Row,
  Col,
  Select,
  InputNumber,
  DatePicker
} from "antd";

const tailLayout = {
  wrapperCol: { offset: 8, span: 16 }
};
const { Option } = Select;
const PersonDarawer = ({ visible, title, onClose, onhandleAdd }) => {
  return (
    <Drawer
      closable={false}
      title={title}
      width={500}
      visible={visible}
      onClose={onClose}
    >
      <Form name="basic" onFinish={onhandleAdd} hideRequiredMark form={form}>
        <Form.Item
          label={firstName}
          name={firstName}
          rules={[{ required: true, message: `Please input ${firstName}` }]}
        >
          <Input allowClear />
        </Form.Item>

        <Form.Item
          label={lastName}
          name={lastName}
          rules={[{ required: true, message: `Please input ${lastName}` }]}
        >
          <Input allowClear />
        </Form.Item>

        <Form.Item
          label={gender}
          name={gender}
          rules={[{ required: true, message: `Please choose ${gender}` }]}
        >
          <Select>
            <Option default value="Male">
              Male
            </Option>
            <Option value="Female">Female</Option>
          </Select>
        </Form.Item>

        <Form.Item
          label={Address}
          name={Address}
          rules={[{ required: true, message: `Please input ${Address}` }]}
        >
          <Select>
            <Option value="Huangpu">Huangpu</Option>

            <Option value="Changning">Changning</Option>

            <Option value="Jing'an">Jing'an</Option>

            <Option value="Putuo">Putuo</Option>

            <Option value="Hongkou">Hongkou</Option>

            <Option value="Yangpu">Yangpu</Option>

            <Option value="Minhang">Minhang </Option>

            <Option value="Baoshan"> Baoshan</Option>

            <Option value="Jiading"> Jiading</Option>

            <Option value="Pudong"> Pudong</Option>

            <Option value="Jinshan"> Jinshan</Option>

            <Option value="Songjiang"> Songjiang</Option>

            <Option value="Qingpu"> Qingpu</Option>

            <Option value="Fengxian"> Fengxian</Option>

            <Option value="Chongming"> Chongming</Option>
          </Select>
        </Form.Item>

        <Form.Item
          label={password}
          name={password}
          rules={[{ required: true, message: `Please input ${password}` }]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item
          label={phoneNumber}
          name={phoneNumber}
          rules={[{ required: true, message: `Please input ${phoneNumber}` }]}
        >
          <InputNumber min={0} className="w-100" />
        </Form.Item>

        <Form.Item
          label={workPosition}
          name={workPosition}
          rules={[{ required: true, message: `Please choose ${workPosition}` }]}
        >
          <Select>
            <Option default value="Chef">
              Chef
            </Option>
            <Option value="Waiter">Waiter</Option>
            <Option value="Manager">Manager</Option>
            <Option value="Cleaner">Cleaner</Option>
          </Select>
        </Form.Item>

        <Form.Item
          label={age}
          name={age}
          rules={[{ required: true, message: `Please input ${age}` }]}
        >
          <InputNumber min={0} max={60} className="w-100" />
        </Form.Item>

        <Form.Item
          label={salary}
          name={salary}
          rules={[{ required: true, message: `Please input ${salary}` }]}
        >
          <InputNumber min={0} max={50000} className="w-100" />
        </Form.Item>

        <Form.Item
          name={email}
          label={email}
          rules={[{ required: true, type: "email" }]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          name={date}
          label={date}
          rules={[{ required: true, message: `Please choose ${date}` }]}
        >
          <DatePicker className="w-100" />
        </Form.Item>
        <Row>
          <Col span={5}>
            <Form.Item {...tailLayout}>
              <Button type="primary" htmlType="submit">
                Add
              </Button>
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item {...tailLayout}>
              <Button type="danger" onClick={onClose}>
                Cancel
              </Button>
            </Form.Item>
          </Col>
          <Col offset={2}>
            <Form.Item>
              <Button
                onClick={() => {
                  form.resetFields();
                }}
              >
                Clear
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
      />
    </Drawer>
  );
};

export default PersonDarawer;
