import React from "react";

import {
  Drawer,
  Input,
  InputNumber,
  Button,
  Form,
  Row,
  Col,
  DatePicker
} from "antd";

const ReservationDrawer = ({
  visible,
  userName,
  tableNumber,
  seats,
  onClose,
  onNameChange,
  onNumberChange,
  onhandleAdd,
  onDataChange,
  onSeatsChange
}) => {
  return (
    <Drawer
      title="Reservation"
      width={420}
      closable={false}
      visible={visible}
      onClose={onClose}
    >
      <Form>
        <Form.Item label="Enter Customer name">
          <Input
            placeholder="Enter name"
            value={userName}
            onChange={onNameChange}
          />
        </Form.Item>
        <Form.Item label="Enter Table Number">
          <InputNumber
            placeholder="Enter Table number"
            value={tableNumber}
            onChange={onNumberChange}
            style={{ width: "370px" }}
          />
        </Form.Item>
        <Row gutter={20}>
          <Col span={10}>
            <Form.Item label="Seats">
              <InputNumber
                value={seats}
                style={{ width: "150px" }}
                onChange={onSeatsChange}
              />
            </Form.Item>
          </Col>
          <Col span={10}>
            <Form.Item label="Data">
              <DatePicker showTime onChange={onDataChange} onOk={false} />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col span={8}>
            <Button type="primary" onClick={onhandleAdd}>
              Add
            </Button>
          </Col>
          <Col span={8}>
            <Button type="danger" onClick={onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default ReservationDrawer;
