import React, { useState, useEffect } from "react";
import { VictoryPie } from "victory";
import { Badge, Avatar, Layout } from "antd";

import { getUser } from "../../../../services/userServices";
import { getDishes } from "../../../../services/dishServices";

const Charts = () => {
  const [user, setUser] = useState([]);
  const [dish, setDish] = useState([]);

  const fetchUser = async () => {
    await getUser()
      .then(res => res.data)
      .then(data => setUser(data));
  };

  const fetchDish = async () => {
    await getDishes()
      .then(res => res.data)
      .then(data => setDish(data));
  };
  useEffect(() => {
    fetchUser();
    fetchDish();
  }, []);

  return (
    <Layout.Content style={{ margin: "2em 16px" }}>
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <div className="container d-flex">
          <VictoryPie
            colorScale={user.length === 0 ? "red" : "cool"}
            height={200}
            animate={{
              duration: 2000
            }}
            data={[
              { x: "User", y: user.length },
              { x: "Dish", y: dish.length }
            ]}
          />
          <div className="d-flex flex-column">
            <Badge count={user.length}>
              <Avatar
                shape="square"
                style={
                  user.length === 0
                    ? { color: "#cc444b" }
                    : { background: "#ee6d52" }
                }
                size="large"
              >
                User
              </Avatar>
            </Badge>
            <Badge className="mt-5" count={dish.length}>
              <Avatar
                shape="square"
                style={{ background: "#cc444b" }}
                size="large"
              >
                Dish
              </Avatar>
            </Badge>
          </div>
        </div>
      </div>
    </Layout.Content>

    // categories={{ x: ["dogs", "cats", "mice"] }}
    //
  );
};

export default Charts;
