// React
import React, { Fragment } from "react";

// Third part
import { Layout, Menu, Typography } from "antd";
import { HashRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from "react-router-dom";

// Custom
import { Order, Reservation, Information } from "./components/";

import { Footer } from "../custom/";

const { Content } = Layout;
const { Header } = Layout;
const { Text } = Typography;

const EmployeeBoard = ({ history, match }) => {
  return (
    <Fragment>
      <Router>
        <Layout style={{ minHeight: "100vh" }}>
          <Header
            style={{
              position: "fixed",
              zIndex: 1,
              width: "100%",
              background: "#000000"
            }}
          >
            <Menu
              theme="dark"
              mode="horizontal"
              style={{ lineHeight: "64px", background: "black" }}
              selectedKeys="1"
            >
              <Menu.Item disabled>
                <Text
                  className="border border border-warning p-3 rounded"
                  type="warning"
                >
                  {match.params.name}
                </Text>
              </Menu.Item>
              <Menu.Item key="1">
                Order
                <Link to="/order" />
              </Menu.Item>
              <Menu.Item key="/reservation">
                Reservation
                <Link to="/reservation" />
              </Menu.Item>
              <Menu.Item key="3">
                Info
                <Link to="/info" />
              </Menu.Item>
              <Menu.Item key="4" onClick={() => history.replace("/")}>
                Logout
              </Menu.Item>
            </Menu>
          </Header>

          <Content style={{ padding: "0 50px", marginTop: 94 }}>
            <Switch>
              <Route path="/order" component={Order} />
              <Route path="/reservation" component={Reservation} />
              <Route path="/info" component={Information} />
            </Switch>
          </Content>
          <Footer />
        </Layout>
      </Router>
    </Fragment>
  );
};
export default EmployeeBoard;

