// React
import React, { Component, Fragment } from "react";

// Third part
import { Form, Icon, Input, Button, Row, Col, Typography } from "antd";
import https from "../services/httpService";
import { apiUrl } from "../config.json";
// custom
import "../assets/css/LoginForm.css";

const { Title } = Typography;

function hasErrors(fieldsError) {
  return Object.keys(fieldsError).some(field => fieldsError[field]);
}

class SignInForm extends Component {
  state = {
    database: []
  };

  async componentDidMount() {
    // To disable submit button at the beginning.
    this.props.form.validateFields();
    https.get(apiUrl).then(res => {
      this.setState({ database: res.data });
      console.log("database", this.state.database);
    });
  }

  handleSubmit = async e => {
    const { database } = this.state;
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      // console.log(database[0]);
      if (
        !err &&
        values.username === database[0].username &&
        values.password === database[0].password
      ) {
        console.log("here");
      }
    });
  };

  render() {
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const {
      getFieldDecorator,
      getFieldsError,
      getFieldError,
      isFieldTouched
    } = this.props.form;

    // Only show error after a field is touched.
    const usernameError =
      isFieldTouched("username") && getFieldError("username");
    const passwordError =
      isFieldTouched("password") && getFieldError("password");
    return (
      <Fragment>
        <Row gutter={20}>
          <Col span={11}>
            <div>
              <img
                alt="restaurant"
                style={{ width: "100%" }}
                src="https://images.unsplash.com/photo-1537047902294-62a40c20a6ae?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1275&q=80"
              />
            </div>
          </Col>
          <Col span={6} offset={3}>
            <Form {...formItemLayout} onSubmit={this.handleSubmit}>
              <Title>Welcome to Restaurant</Title>
              <Form.Item
                validateStatus={usernameError ? "error" : ""}
                help={usernameError || ""}
              >
                {getFieldDecorator("username", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your username!"
                    }
                  ]
                })(
                  <Input
                    prefix={
                      <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="Username"
                    style={{ width: "500px" }}
                  />
                )}
              </Form.Item>

              <Form.Item
                validateStatus={passwordError ? "error" : ""}
                help={passwordError || ""}
              >
                {getFieldDecorator("password", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your Password!"
                    }
                  ]
                })(
                  <Input
                    prefix={
                      <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    type="password"
                    placeholder="Password"
                    style={{ width: "500px" }}
                  />
                )}
              </Form.Item>

              <Form.Item>
                <Button
                  type="primary"
                  htmlType="submit"
                  disabled={hasErrors(getFieldsError())}
                  block
                >
                  Log in
                </Button>
              </Form.Item>
            </Form>
          </Col>
        </Row>
      </Fragment>
    );
  }
}
const WrappedHorizontalLoginForm = Form.create({ name: "horizontal_login" })(
  SignInForm
);
export default WrappedHorizontalLoginForm;
