import React from "react";
import { Form, Input, InputNumber } from "antd";

const CustomizedForm = ({
  form,
  data,
  firstData,
  secondData,
  thirdData,
  fourthData
}) => (
  <Form
    form={form}
    layout="vertical"
    name="form_in_modal"
    initialValues={{
      name: data.name,
      price: data.price,
      type: data.type,
      date: data.date
    }}
  >
    <Form.Item
      name={firstData}
      label={firstData}
      rules={[
        {
          required: true,
          message: `Please input the ${firstData}!`
        }
      ]}
    >
      <Input />
    </Form.Item>
    <Form.Item
      name={secondData}
      label={secondData}
      rules={[
        {
          required: true,
          message: `Please input the ${secondData}!`
        }
      ]}
    >
      <InputNumber min={0} formatter={replace(/\B(?=(\d{3})+(?!\d))/g, ",")} />
    </Form.Item>
    <Form.Item
      name={thirdData}
      label={thirdData}
      rules={[
        {
          required: true,
          message: `Please input the ${thirdData}!`
        }
      ]}
    >
      <Input />
    </Form.Item>
    <Form.Item
      name={fourthData}
      label={fourthData}
      rules={[
        {
          required: true,
          message: `Please input the ${fourthData}!`
        }
      ]}
    >
      <Input />
    </Form.Item>
  </Form>
);
export default CustomizedForm;
