import React, { useState, Fragment } from "react";

import { apiUrl } from "../../config.json";
import { Form, Input, Layout, Typography, Row, Col, Button, Card } from "antd";

// import { openNotification } from "../custom";
import httpService from "../../services/httpService";
import { openNotification, HeaderTitle, Footer } from "../custom/";

const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 }
};

const RememberPassword = ({ history }) => {
  const [newPassword, setNewPassword] = useState([]);
  const onhandleSend = item => {
    try {
      httpService
        .post(apiUrl + "user/remember", item)
        .then(res => res.data.data)
        .then(data => setNewPassword(data));
    } catch (error) {
      openNotification("error", "Can't send email", "Check connection");
    }
  };

  return (
    <Fragment>
      <Layout>
        <Layout.Content style={{ margin: "5em 16px" }}>
          <div
            style={{
              background: "#fff",
              padding: 44,
              minHeight: 380
            }}
            className="border border-info"
          >
            <div className="mb-5">
              <HeaderTitle title={"Remember Password"} />
            </div>
            <Row>
              <Col xs={10} sm={10} md={10} lg={10} xl={10}>
                <Form hideRequiredMark onFinish={onhandleSend}>
                  <Form.Item
                    name="email"
                    label="Email"
                    rules={[
                      {
                        required: true,
                        type: "email",
                        message: "Enter correct email address"
                      }
                    ]}
                  >
                    <Input />
                  </Form.Item>
                  <Row>
                    <Col span={4} offset={2}>
                      <Form.Item>
                        <Button type="primary" htmlType="submit">
                          Send
                        </Button>
                      </Form.Item>
                    </Col>
                    <Col>
                      <Form.Item>
                        <Button
                          type="ghost"
                          onClick={() => history.replace("/")}
                        >
                          Back to Home
                        </Button>
                      </Form.Item>
                    </Col>
                  </Row>
                </Form>
              </Col>
              <Col xs={{ span: 12, offset: 2 }} sm={10} md={10} lg={10} xl={10}>
                <Card
                  style={{ width: "200px" }}
                  className="text-center rounded border border-warning"
                  title="New password"
                  cover={
                    <Typography.Title
                      className="mt-4 border border-info bg-light"
                      level={4}
                    >
                      {newPassword}
                    </Typography.Title>
                  }
                >
                  <Card.Meta
                    title="This is your new password"
                    description="Dont forget to change your password in your account"
                  />
                </Card>
              </Col>
            </Row>
          </div>
        </Layout.Content>
      </Layout>

      <Footer />
    </Fragment>
    // <Fragment>
    //   <Layout>
    //     <Layout.Content style={{ margin: "5em 16px" }}>
    //       <div
    //         style={{
    //           background: "#fff",
    //           padding: 44,
    //           minHeight: 380
    //         }}
    //         className="border border-info"
    //       >
    //         <Form hideRequiredMark onFinish={onhandleSend}>
    //           <Row>
    //             <Col xs={14}>
    //               <Row>
    //                 <Typography.Title className="mb-5">
    //                   Remember Password
    //                 </Typography.Title>
    //               </Row>
    //               <Row>
    //                 <Col xs={10}>
    //
    //                 </Col>
    //                 <Col>
    //                   <Form.Item>
    //                     <Button type="primary" htmlType="submit">
    //                       Send
    //                     </Button>
    //                   </Form.Item>
    //                 </Col>
    //                 <Col>
    //                   <Form.Item>
    //                     <Button
    //                       type="primary"
    //                       onClick={() => history.replace("/")}
    //                     >
    //                       Back to Home
    //                     </Button>
    //                   </Form.Item>
    //                 </Col>
    //               </Row>
    //             </Col>
    //             <Col offset={5}>
    //
    //             </Col>
    //           </Row>
    //         </Form>
    //       </div>
    //     </Layout.Content>
    //   </Layout>

    //
    // </Fragment>
  );
};

export default RememberPassword;
