// React
import React, { Fragment, useState, useEffect } from "react";

// third part
import {
  Drawer,
  Form,
  Select,
  Input,
  TimePicker,
  Button,
  Row,
  Col,
  Table,
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";

// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 24 },
};

const OrderDrawer = ({ visible, onClose, handleAdd }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then((res) => res.data)
        .then((data) => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then((res) => res.data)
        .then((data) => setOrders(data));
    } catch (error) {}
  };

  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
      width={900}
    >
      <Form>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="Select table number">
            {tableNumber.map((table) => (
              <Select.Option key={table.id} value={table.table_number}>
                {table.table_number}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Table dataSource={orders}>
          <Form.Item name="dish">
            <Table.Column title="Name" dataIndex="name" />
          </Form.Item>
          <Form.Item name="quantitiy">
            <Table.Column
              title="Quantity"
              width={20}
              render={(record) => (
                <Fragment>
                  <Input />
                </Fragment>
              )}
            />
          </Form.Item>
        </Table>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
