import React, { Component } from "react";

import {
  Card,
  Icon,
  Avatar,
  Row,
  Col,
  Drawer,
  Button,
  Input,
  InputNumber,
  Select,
  Divider
} from "antd";

const { Meta } = Card;
const { Option } = Select;

export default class Order extends Component {
  state = {
    data: [
      {
        id: 1,
        table_number: 1,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Alex",
        order: ["burger,", "cheese,", "free"]
      },
      {
        id: 2,
        table_number: 2,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Martin",
        order: ["burger,", "cheese,", "free"]
      },
      {
        id: 3,
        table_number: 3,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "John",
        order: ["burger,", "cheese,", "free"]
      },
      {
        id: 4,
        table_number: 4,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Maria",
        order: ["burger,", "cheese,", "free"]
      },
      {
        id: 5,
        table_number: 5,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Luci",
        order: ["burger,", "cheese,", "free"]
      },
      {
        id: 6,
        table_number: 6,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Isabel",
        order: ["burger,", "cheese,", "free"]
      }
    ],
    visible: false,
    id: 0,
    table_number: "",
    name: "",
    order: []
  };

  showDrawer = () => {
    this.setState({ visible: true });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };

  numberChange = number => {
    this.setState({ table_number: number });
  };

  nameChange = event => {
    this.setState({ name: event.target.value });
  };

  orderSelect = value => {
    this.setState({
      order: value
    });
  };

  handleOrder = () => {
    const { id, data, table_number, name, order } = this.state;
    const newData = {
      id: id,
      table_number: table_number,
      name: name,
      order: order
    };
    this.setState({
      data: [...data, newData],
      id: id + 1
    });
  };

  handleDelete = dataId => {
    const data = this.state.data.filter(item => item.id !== dataId);
    this.setState({ data: data });
  };
  render() {
    const { data } = this.state;
    return (
      <div
        style={{
          background: "#fff",
          padding: 24,
          minHeight: 680
        }}
      >
        <Row>
          <Button
            type="primary"
            style={{ width: "150px", float: "right" }}
            onClick={this.showDrawer}
          >
            <Icon type="plus" />
            New Order
          </Button>
        </Row>
        <Drawer
          title="Add new Order"
          width={520}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
        >
          <Row gutter={[40, 20]}>
            <Col span={10}>
              <InputNumber
                placeholder="Enter table number"
                style={{ width: "200px" }}
                value={this.state.table_number}
                onChange={this.numberChange}
              />
            </Col>
            <Col span={10}>
              <Input
                placeholder="Enter customer name"
                value={this.state.name}
                onChange={this.nameChange}
              />
            </Col>
          </Row>
          <Row gutter={[20, 20]}>
            <Col span={10}>
              <Select
                mode="multiple"
                style={{ width: "100%" }}
                placeholder="Order"
                optionLabelProp="label"
                onChange={this.orderSelect}
              >
                <Option value="burger" label="burger">
                  burger
                </Option>
                <Option value="cheese" label="Cheese">
                  Cheese
                </Option>
                <Option value="FrenchFree" label="FrenchFree">
                  FrenchFree
                </Option>
              </Select>
            </Col>
          </Row>
          <Row>
            <Divider />
            <Col span={6}>
              <Button
                onClick={() => this.setState({ visible: false })}
                style={{ width: "100px" }}
                type="danger"
              >
                Cancel
              </Button>
            </Col>
            <Col span={6}>
              <Button
                style={{ width: "100px" }}
                type="primary"
                onClick={this.handleOrder}
              >
                Order
              </Button>
            </Col>
          </Row>
        </Drawer>
        <Row gutter={2}>
          <Divider />
          {data.map(a => (
            <Col span={6}>
              <Card
                key={a.id}
                id={a.id}
                style={{ width: 300, margin: "2em 2em" }}
                title={a.table_number}
                cover={<img alt="" src={a.url} />}
                actions={[
                  <Icon type="delete" onClick={() => this.handleDelete(a.id)} />
                ]}
              >
                <Meta
                  avatar={<Avatar>{a.name}</Avatar>}
                  title={a.name}
                  description={a.order}
                />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
    );
  }
}
