// React
import React, { useEffect, useState } from "react";

// third part
import { Modal, Form, TreeSelect, Select, Input, TimePicker } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";
// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 5
  },
  wrapperCol: {
    span: 16
  }
};
const EditModal = ({ editableData, visible, handleUpdate, onClose }) => {
  const [form] = Form.useForm();
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then(res => res.data)
        .then(data => setOrders(data));
    } catch (error) {}
  };
  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onClose}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            handleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        {...layout}
        hideRequiredMark
        onFinish={handleUpdate}
        form={form}
        initialValues={{
          id: editableData.id,
          table_number: editableData.table,
          customer_name: editableData.customer,
          dish: editableData.ordered_items
        }}
      >
        <Form.Item name="id">
          <h1>Edit Orders</h1>
        </Form.Item>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="select table number">
            {tableNumber.map(table => (
              <Select.Option key={table.id} value={table.table_number}>
                {table.table_number}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item
          label="Customer"
          name="customer_name"
          rules={[{ required: true, message: "Enter Customer Name" }]}
        >
          <Input placeholder="Customer Name" allowClear />
        </Form.Item>
        <Form.Item label="Order" name="dish">
          <TreeSelect
            style={{ width: "100%" }}
            dropdownStyle={{ maxHeight: 400, overflow: "auto" }}
            placeholder="Please select"
            allowClear
            multiple
            showSearch
            autoClearSearchValue
            bordered
          >
            {orders.map(or => (
              <TreeSelect.TreeNode
                key={or.id}
                value={or.type}
                title={or.type}
                selectable={false}
              >
                <TreeSelect.TreeNode
                  key={orders.id}
                  value={or.name}
                  title={or.name}
                />
              </TreeSelect.TreeNode>
            ))}
          </TreeSelect>
        </Form.Item>
        <Form.Item label="Price" name="price">
          <TreeSelect
            showSearch
            style={{ width: "100%" }}
            placeholder="Please select Price"
            allowClear
            multiple
            bordered
          >
            {orders.map(or => (
              <TreeSelect.TreeNode
                key={orders.id}
                selectable={false}
                title={or.name}
                value={or.name}
              >
                <TreeSelect.TreeNode
                  key={orders.id}
                  title={or.price}
                  value={or.price}
                />
              </TreeSelect.TreeNode>
            ))}
          </TreeSelect>
        </Form.Item>
        <Form.Item
          label="Time"
          name="time"
          placeholder="select time"
          rules={[{ required: true, message: "Enter ordered time" }]}
        >
          <TimePicker />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default EditModal;
