import React from "react";
import { Drawer, Input, InputNumber, Select, Button, Form } from "antd";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange
}) => {
  return (
    <Drawer
      width={420}
      title="Basic Drawer"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form>
        <Form.Item>
          <Input
            placeholder="name"
            value={name}
            onChange={namehandleChange}
            allowClear
          />
        </Form.Item>
        <Form.Item></Form.Item>
        <InputNumber
          placeholder="price"
          value={price}
          onChange={priceHandleChange}
          style={{ width: "250px" }}
        />
        <Form.Item></Form.Item>
        <Select
          onChange={typehandleChange}
          defaultValue="select type"
          style={{ width: "250px" }}
        >
          <Option value="snack🍿">Snack</Option>
          <Option value="soup🍜">Soup</Option>
          <Option value="desert🍮">Desert</Option>
        </Select>
        <Form.Item></Form.Item>
        <Button
          onClick={handleAdd}
          type="primary"
          style={{ marginBottom: 16, width: "100px" }}
        >
          Add
        </Button>
      </Form>
    </Drawer>
  );
};

export default Drawers;
