import React, { useState, Fragment } from "react";
import { PrinterOutlined } from "@ant-design/icons";
import Pdf from "react-to-pdf";
import { Link } from "react-router-dom";
import { Layout, Typography, Row, Col } from "antd";

const ref = React.createRef();

const PrintBill = ({ location }) => {
  const [data] = useState(location.state);
  return (
    <Fragment>
      <Layout.Content style={{ margin: "2em 16px" }}>
        <div
          style={{ background: "#fff", padding: 24, minHeight: 280 }}
          className="border border-dark rounded"
        >
          <div
            style={{ width: 500, height: 500 }}
            className="container border border-dark rounded"
            ref={ref}
          >
            <Row>
              <Col span={10}>
                <Typography.Title className="text-center pt-3">{`Table: ${41}`}</Typography.Title>
              </Col>
              <Col span={10} offset={4}>
                <Typography.Title
                  level={4}
                  className="text-right pt-3"
                >{`Customer: ${"Mike"}`}</Typography.Title>
              </Col>
            </Row>

            <ul>
              <Row>
                <Col span={14}>
                  {/* {data.orders.map((order) => (
                    <li style={{ listStyleType: "none" }}>{order}........</li>
                  ))} */}
                  <li>burger</li>
                </Col>
                <Col span={10}>
                  {/* {data.price.map((price) => (
                    <li style={{ listStyleType: "none" }}>{price}</li>
                  ))} */}
                  <li>15$</li>
                </Col>
              </Row>
            </ul>
          </div>
          <div>
            <Pdf targetRef={ref} filename="code-example.pdf">
              {({ toPdf }) => (
                <PrinterOutlined onClick={toPdf}>Print</PrinterOutlined>
              )}
            </Pdf>
          </div>
          <Link to="/info">Back</Link>
        </div>
      </Layout.Content>
    </Fragment>
  );
};
export default PrintBill;
