import React from "react";
import { Modal, Form, Input, InputNumber } from "antd";
const CustomModal = ({
  visible,
  handleOk,
  handleClose,
  editableData,
  handleTableNumberChange,
  handleCustomerChange,
  handleSeatsChange,
  handleDateChange
}) => {
  return (
    <Modal
      visible={visible}
      onOk={() => handleOk(editableData)}
      onCancel={handleClose}
    >
      <Form>
        <Form.Item label="Table Number">
          <InputNumber
            style={{ width: "470px" }}
            value={editableData.tableNumber}
            onChange={handleTableNumberChange}
          />
        </Form.Item>
        <Form.Item label="CustomerName">
          <Input
            value={editableData.usernameValue}
            onChange={handleCustomerChange}
          />
        </Form.Item>
        <Form.Item label="Reserved Seats">
          <InputNumber
            style={{ width: "470px" }}
            value={editableData.seats}
            onChange={handleSeatsChange}
          />
        </Form.Item>
        <Form.Item label="Date">
          <Input
            style={{ width: "470px" }}
            value={editableData.date}
            onChange={handleDateChange}
          />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default CustomModal;
