// React
import React, { useState, useEffect } from "react";

// Third part
import { Layout, Menu } from "antd";
import { LogoutOutlined } from "@ant-design/icons";
import { Chart, Geom, Axis, Tooltip, Legend, Coord } from "bizcharts";
import { HashRouter as Router, Route, Link, Switch } from "react-router-dom";

// Custom
import { Breakfast, PersonalIcon, TableInfo } from "../../assets/";
import { Dish, Personal, Table } from "./components/";
import { Footer } from "../custom/";
const { Header, Content } = Layout;

const AdminBoard = ({ match, history }) => {
  const [employeeChart, setEmployeeChart] = useState([]);
  const data = [
    { genre: "Sports", sold: 275, income: 2300 },
    { genre: "Strategy", sold: 115, income: 667 },
    { genre: "Action", sold: 120, income: 982 },
    { genre: "Shooter", sold: 350, income: 5271 },
    { genre: "Other", sold: 150, income: 3710 }
  ];

  const cols = {
    sold: { alias: "销售量" },
    genre: { alias: "游戏种类" }
  };

  return (
    <Router>
      <Layout style={{ minHeight: "100vh" }}>
        <Header
          className="header"
          style={{
            position: "fixed",
            zIndex: 1,
            width: "100%",
            background: "#ffffff"
          }}
        >
          <Menu
            mode="horizontal"
            style={{ height: "100%", textAlign: "center" }}
          >
            <Menu.Item disabled>
              <div className="text-uppercase font-weight-bold">
                {match.params.name}
              </div>
            </Menu.Item>
            <Menu.Item key="1">
              <Breakfast />
              <span className=" menu-title ml-2">Dish Information</span>
              <Link to="/dish" />
            </Menu.Item>

            <Menu.Item key="2">
              <PersonalIcon />
              <span className="menu-title ml-2">Personal</span>
              <Link to="/personal" />
            </Menu.Item>
            <Menu.Item key="3">
              <TableInfo />
              <span className=" menu-title ml-2">Table</span>
              <Link to="/table" />
            </Menu.Item>
            <Menu.Item key="4" onClick={() => history.replace("/")}>
              <LogoutOutlined style={{ fontSize: "20px" }} />
              <span className="menu-title ml-2">Logout</span>
            </Menu.Item>
          </Menu>
        </Header>

        <Content
          className="site-layout-background"
          style={{ padding: "0 50px", marginTop: 64 }}
        >
          <div
            className="site-layout-background"
            style={{ padding: 24, minHeight: 380 }}
          >
            <Chart width={600} height={400} data={data} scale={cols}>
              <Axis name="genre" title />
              <Axis name="sold" title />
              <Legend position="top" dy={-20} />
              <Tooltip />
              <Geom type="interval" position="genre*sold" color="genre" />
            </Chart>
            <Switch>
              <Route path="/dish" component={Dish} />
              <Route path="/personal" component={Personal} />
              <Route path="/table" component={Table} />
            </Switch>
          </div>
        </Content>
        <Footer />
      </Layout>
    </Router>
  );
};

export default AdminBoard;
