import React, { Fragment } from "react";
import { Table, Button, Row, Col } from "antd";

const { Column } = Table;
const TableT = ({ data, deleteTableItems, editItems }) => {
  return (
    <Table
      dataSource={data}
      rowKey={data => data.id}
      bordered
      size="middle"
      scroll={{ x: 1300 }}
    >
      <Column
        title="TableNumber"
        dataIndex="table_number"
        defaultSortOrder="ascend"
        sorter={(a, b) => a.table_number - b.table_number}
      />
      <Column
        title="Seats"
        dataIndex="seats"
        defaultSortOrder="ascend"
        sorter={(a, b) => a.seats - b.seats}
      />
      <Column
        title="Date"
        dataIndex="date"
        sorter={(a, b) => a.date.length - b.date.length}
      />
      <Column
        fixed="right"
        title="Actions"
        width={80}
        render={record => (
          <Fragment>
            <Row>
              <Col span={6}>
                <Button
                  type="danger"
                  className="w-20"
                  onClick={() => deleteTableItems(record.id)}
                >
                  delete
                </Button>
              </Col>
              <Col offset={2}>
                <Button
                  className="mt-1"
                  type="primary"
                  onClick={() => editItems(record)}
                >
                  Edit
                </Button>
              </Col>
            </Row>
          </Fragment>
        )}
      />
    </Table>
  );
};

export default TableT;
