import React, { useState } from "react";
import { PrinterOutlined } from "@ant-design/icons";
import Pdf from "react-to-pdf";
import { Page, Text, View, Document, StyleSheet } from "@react-pdf/renderer";
import { Link } from "react-router-dom";
const ref = React.createRef();
const styles = StyleSheet.create({
  page: {
    flexDirection: "row",
    backgroundColor: "#E4E4E4",
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,
  },
});
const PrintBill = ({ location }) => {
  const [data] = useState(location.state);
  return (
    <div className="App">
      <div style={{ width: 500, height: 500 }} className="container" ref={ref}>
        <h1>{data.table}</h1>
        <h2>{data.cutomer}</h2>
        <ul>
          <div className="row">
            <div className="col">
              {data.orders.map((order) => (
                <li style={{ listStyleType: "none" }}>{order}........</li>
              ))}
            </div>
            <div className="col">
              {data.price.map((price) => (
                <li style={{ listStyleType: "none" }}>{price}</li>
              ))}
            </div>
          </div>
        </ul>
        <h4></h4>
      </div>
      <div>
        <Pdf targetRef={ref} filename="code-example.pdf">
          {({ toPdf }) => (
            <PrinterOutlined onClick={toPdf}>Print</PrinterOutlined>
          )}
        </Pdf>
      </div>
      <Link to="/info">Back</Link>
    </div>
  );
};

export default PrintBill;
