import React from "react";
import {
  Drawer,
  Input,
  InputNumber,
  Select,
  Button,
  Form,
  Row,
  Col,
  DatePicker
} from "antd";
const { Option } = Select;

const Drawers = ({ visible, onClose, handleSubmit, form }) => {
  const { getFieldDecorator } = form;
  return (
    <Drawer
      width={420}
      title="AddItem"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form onSubmit={handleSubmit}>
        <Form.Item>
          {getFieldDecorator("name", {
            rules: [
              {
                required: true,
                message: "Please Input Dish name!"
              }
            ]
          })(<Input placeholder="Enter Dish name" allowClear />)}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator("price", {
            rules: [
              {
                required: true,
                message: "Please Dish price!"
              }
            ]
          })(
            <InputNumber
              placeholder="Enter dish price"
              style={{ width: "370px" }}
              allowClear
            />
          )}
        </Form.Item>

        <Form.Item>
          {getFieldDecorator("type", {
            rules: [
              {
                required: true,
                message: "Please Dish price!"
              }
            ]
          })(
            <Select placeholder="choose dish type">
              <Option value="snackðŸ¿">Snack</Option>
              <Option value="soupðŸœ">Soup</Option>
              <Option value="desertðŸ®">Desert</Option>
            </Select>
          )}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator("date", {
            rules: [
              {
                required: true,
                message: "Please Dish date!"
              }
            ]
          })(<DatePicker showTime style={{ width: "370px" }} />)}
        </Form.Item>
        <Row>
          <Col span={8}>
            <Form.Item>
              <Button htmlType="submit" type="primary">
                Add
              </Button>
            </Form.Item>
          </Col>
          <Col span={8}>
            <Form.Item>
              <Button onClick={onClose} type="danger">
                Cancel
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};
const WrappedDishForm = Form.create({ name: "register" })(Drawers);
export default WrappedDishForm;
