import React from "react";

import { Modal, Row, Col, Form, Input, InputNumber, Typography } from "antd";

const DishModal = ({ visible, data, onhandleUpdate, onCancel }) => {
  const [form] = Form.useForm();
  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onCancel}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            onhandleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        hideRequiredMark
        form={form}
        layout="vertical"
        name="form_in_modal"
        initialValues={{
          id: data.id,
          name: data.name,
          price: data.price,
          type: data.type
        }}
      >
        <Form.Item name="id">
          <Typography level={4}>Edit Element</Typography>
        </Form.Item>
        <Row>
          <Col span={15}>
            <Form.Item
              name="name"
              label="Name"
              rules={[
                {
                  required: true,
                  message: "Please input the name!"
                }
              ]}
            >
              <Input />
            </Form.Item>
          </Col>
          <Col span={5} offset={2}>
            <Form.Item
              name="price"
              label="Price"
              rules={[
                {
                  required: true,
                  message: "Please input the price!"
                }
              ]}
            >
              <InputNumber min={0} step={0.1} />
            </Form.Item>
          </Col>
        </Row>
        <Form.Item
          name="type"
          label="type"
          rules={[
            {
              required: true,
              message: "Please input the type!"
            }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="date"
          label="Date"
          rules={[
            {
              required: true,
              message: "Please input the date!"
            }
          ]}
        >
          <Input />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default DishModal;
