// React
import React, { Fragment, useEffect, useState } from "react";

// Third Part
import { Layout } from "antd";

// Custom
import {
  HeaderTitle,
  CustomTable,
  CustomModal,
  openNotification
} from "../common";
// Services
import {
  getDishes,
  addDishes,
  deleteDishes,
  updateDishes
} from "../../services/dishServices";

const { Content } = Layout;

const CustomContent = () => {
  // initializing states
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState();
  const [editData, setEditData] = useState({});

  //   hook cycles
  const fetchDishData = async () => {
    await getDishes()
      .then(res => res.data)
      .then(res => setData(res))
      .catch(err =>
        openNotification(
          "error",
          "Cannot get informations",
          "Please check connection"
        )
      );
  };

  //   Modal handle
  const onCancel = () => {
    setVisible(false);
  };

  const onhandleAdd = async item => {
    console.log(item);
    const orginaldata = { ...item };
    try {
      await updateDishes(data);
    } catch (error) {
      setEditData(orginaldata);
    }
  };

  //   handle actions in table item
  const deleteItem = async item => {
    const orginaldata = data;
    try {
      await deleteDishes(item);
    } catch (error) {
      setData(orginaldata);
    }
  };

  const editItem = async item => {
    const newData = { ...item };
    setEditData(newData);
    setVisible(true);
  };

  useEffect(() => {
    fetchDishData();
  }, []);
  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Dish Menu"} />
          <CustomTable
            data={data}
            colOne={"name"}
            colTwo={"price"}
            colThird={"type"}
            colFourth={"date"}
            colFive={"Actions"}
            deleteItem={deleteItem}
            editItem={editItem}
          />
          <CustomModal
            visible={visible}
            labelOne={"Name"}
            labelTwo={"Price"}
            labelThree={"Type"}
            labelFour={"Date"}
            nameOne={"name"}
            nameTwo={"price"}
            nameThree={"type"}
            nameFour={"date"}
            onhandleAdd={onhandleAdd}
            onCancel={onCancel}
            // editData={editData}
            editOneData={editData.name}
            editSecondData={editData.price}
            editThirdData={editData.type}
            editFourthData={editData.date}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default CustomContent;
