import React, { useState } from "react";
import { Form, Input } from "antd";
const CustomizedForm = ({ onChange, fields }) => (
  <Form
    name="global_state"
    layout="inline"
    fields={fields}
    onFieldsChange={(changedFields, allFields) => {
      onChange(allFields);
    }}
  >
    <Form.Item
      name="name"
      label="Name"
      rules={[
        {
          required: true,
          message: "Name is required!"
        }
      ]}
    >
      <Input />
    </Form.Item>
  </Form>
);
export default CustomizedForm;
