// React
import React, { useState, useEffect } from "react";

// third part
import {
  Drawer,
  Form,
  Row,
  Col,
  Select,
  Input,
  TimePicker,
  Button,
  Cascader
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";

// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 8,
    offset: 1
  },
  wrapperCol: {
    span: 16
  }
};

const OrderDrawer = ({ visible, onClose, handleAdd }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then(res => res.data)
        .then(data => setOrders(data));
    } catch (error) {}
  };

  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
      placement="bottom"
      width={480}
    >
      <Form {...layout} hideRequiredMark onFinish={handleAdd}>
        <Row>
          <Col xs={24} sm={12} md={12} lg={7}>
            <Form.Item
              label="Table Number"
              name="table_number"
              rules={[{ required: true, message: "Enter Table Number" }]}
            >
              <Select placeholder="select table number">
                {tableNumber.map(table => (
                  <Select.Option key={table.id} value={table.table_number}>
                    {table.table_number}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} md={12} lg={6}>
            <Form.Item
              label="Customer"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer Name" }]}
            >
              <Input placeholder="Customer Name" allowClear />
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} lg={6}>
            <Form.Item lable="Orders">
              <Input.Group compact>
                <Form.Item
                  name={["address", "province"]}
                  noStyle
                  rules={[{ required: true, message: "Province is required" }]}
                >
                  <Select placeholder="Select province">
                    <Select.Option value="Zhejiang">Zhejiang</Select.Option>
                    <Select.Option value="Jiangsu">Jiangsu</Select.Option>
                  </Select>
                </Form.Item>
                <Form.Item
                  name={["address", "street"]}
                  noStyle
                  rules={[{ required: true, message: "Street is required" }]}
                >
                  <Input style={{ width: "50%" }} placeholder="Input street" />
                </Form.Item>
              </Input.Group>
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} md={12} lg={5}>
            <Form.Item label="Time" name="time" placeholder="select time">
              <TimePicker className="w-100" />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col xs={4} sm={4} md={3} lg={2} xl={{ span: 2, offset: 3 }}>
            <Button type="primary" htmlType="submit">
              Order
            </Button>
          </Col>
          <Col xs={4} sm={3} md={3}>
            <Button type="danger" onClick={onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
