// React
import React, { useEffect, useState } from "react";

// third part
import { Modal, Form, Row, Col, Select, Input, TimePicker } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";
// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};
const EditModal = ({ editableData, visible, handleUpdate, onClose }) => {
  const [form] = Form.useForm();
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onClose}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            handleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form {...layout} hideRequiredMark onFinish={handleUpdate}>
        form={form}
      </Form>
    </Modal>
  );
};

export default EditModal;
