// react
import React, { Component, Fragment } from "react";

// third part
import { Layout, Menu, Icon } from "antd";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";

// components
import { Dish, Personal, Table } from "./components/";
import Footer from "../common/footer";

// custom
import "../../assets/css/AdminForm.css";

// Icons
import { Breakfast, PersonalIcon, TableInfo } from "../../assets/";

const { Header, Content, Sider } = Layout;

class AdminBoard extends Component {
  state = {
    collapsed: false,
    newdata: {}
  };
  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };

  render() {
    const { history } = this.props;
    return (
      <Fragment>
        <Router>
          <Layout style={{ minHeight: "100vh" }}>
            <Sider trigger={null} collapsible collapsed={this.state.collapsed}>
              <div className="logo">
                <p style={{ textAlign: "center", fontSize: "20px" }}>Admin</p>
              </div>

              <Menu theme="light" mode="inline">
                <Menu.Item key="1">
                  <Breakfast />
                  <span className="menu-title">Dish information</span>
                  <Link to="/dish" />
                </Menu.Item>
                <Menu.Item key="2">
                  <PersonalIcon />
                  <span className="menu-title">Personal Information</span>
                  <Link to="/personal" />
                  {/* <Link
                    to={{ pathname: "/personal", state: this.props.newUser }}
                  /> */}
                </Menu.Item>

                <Menu.Item key="3">
                  <TableInfo />
                  <span className="menu-title">Table Information</span>
                  <Link to="/table" />
                </Menu.Item>
                <Menu.Item>
                  <Icon
                    type="logout"
                    style={{ fontSize: "30px", marginTop: "5px" }}
                    onClick={() => history.replace("/")}
                  />
                  <span
                    className="menu-title"
                    onClick={() => history.replace("/")}
                  >
                    Logout
                  </span>
                </Menu.Item>
              </Menu>
            </Sider>
            <Layout>
              <Header style={{ background: "#000000", padding: 0 }}>
                <Icon
                  className="trigger"
                  type={this.state.collapsed ? "menu-unfold" : "menu-fold"}
                  onClick={this.toggle}
                />
              </Header>
              <Content style={{ margin: "2em 16px" }}>
                <Switch>
                  <Route path="/dish" component={Dish} />
                  <Route path="/personal" component={Personal} />
                  <Route path="/table" component={Table} />
                </Switch>
              </Content>
              <Footer />
            </Layout>
          </Layout>
        </Router>
      </Fragment>
    );
  }
}
export default AdminBoard;
