//React
import React, { Fragment, useState, useEffect } from "react";

// Third part
import { Divider, Input, Card } from "antd";
import { SearchOutlined } from "@ant-design/icons";

// Tools
import _ from "lodash";

// Services
import { getOrder } from "../../../../services/orderServices";

// custom Components
import { openNotification } from "../../../custom/";

const Search = () => {
  // state
  const [orderData, setOrderData] = useState([]);
  const [searchData, setSearchData] = useState({});
  // searchMethod
  const SearchOrders = event => {
    const num = event.target.value;
    const table = _.find(orderData, ["table", num]);
    const newData = { ...table };
    setSearchData(newData);
  };
  // fetch dataMethod
  const getData = async () => {
    try {
      await getOrder()
        .then(res => res.data)
        .then(data => setOrderData(data));
    } catch (err) {
      openNotification("error", "Can't get data", "Check connection");
    }
  };
  // Hook
  useState(() => {
    getData();
  }, []);

  return (
    <Fragment>
      <Input
        min={0}
        prefix={<SearchOutlined />}
        placeholder="Input TableNumber"
        className="w-100"
        onChange={value => SearchOrders(value)}
      />
      <Divider />
      <Card title={searchData.table} cover={searchData.customer}></Card>
    </Fragment>
  );
};

export default Search;
