import React, { Component, Fragment } from "react";
import {
  Layout,
  Table,
  Button,
  InputNumber,
  Input,
  Form,
  Row,
  Col,
  Select
} from "antd";
import { Resizable } from "react-resizable";

import "../../../assets/css/AdminForm.css";

const { Option } = Select;

// for resizing
const ResizeableTitle = props => {
  const { onResize, width, ...restProps } = props;

  if (!width) {
    return <th {...restProps} />;
  }

  return (
    <Resizable
      width={width}
      height={0}
      onResize={onResize}
      draggableOpts={{ enableUserSelectHack: false }}
    >
      <th {...restProps} />
    </Resizable>
  );
};

// for editable
const EditableContext = React.createContext();

class EditableCell extends React.Component {
  getInput = () => {
    if (this.props.inputType === "number") {
      return <InputNumber />;
    }
    return <Input />;
  };

  renderCell = ({ getFieldDecorator }) => {
    const {
      editing,
      dataIndex,
      title,
      inputType,
      record,
      index,
      children,
      ...restProps
    } = this.props;
    return (
      <td {...restProps}>
        {editing ? (
          <Form.Item style={{ margin: 0 }}>
            {getFieldDecorator(dataIndex, {
              rules: [
                {
                  required: true,
                  message: `Please Input ${title}!`
                }
              ],
              initialValue: record[dataIndex]
            })(this.getInput())}
          </Form.Item>
        ) : (
          children
        )}
      </td>
    );
  };

  render() {
    return (
      <EditableContext.Consumer>{this.renderCell}</EditableContext.Consumer>
    );
  }
}

const { Content } = Layout;
class Personal extends Component {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        {
          title: "Name",
          dataIndex: "name",
          editable: true
        },
        {
          title: "Salary",
          dataIndex: "price",
          editable: true
        },
        {
          title: "Type",
          dataIndex: "type",
          editable: true
        },
        {
          title: "Age",
          dataIndex: "age",
          editable: true
        },
        {
          title: "Address",
          dataIndex: "address",
          editable: true
        },
        {
          title: "Phone",
          dataIndex: "phone",
          editable: true
        },
        {
          title: "Action",
          dataIndex: "operation",
          render: (text, record) => {
            const { editingKey } = this.state;
            const editable = this.isEditing(record);
            return editable ? (
              <span>
                <EditableContext.Consumer>
                  {form => (
                    <Row gutter={20}>
                      <Col span={9}>
                        <Button
                          size="small"
                          onClick={() => this.save(form, record.key)}
                          style={{ width: "60px" }}
                        >
                          Save
                        </Button>
                      </Col>
                      <Col span={9}>
                        <Button
                          size="small"
                          style={{ width: "70px" }}
                          onClick={() => this.cancel(record.key)}
                        >
                          Cancel
                        </Button>
                      </Col>
                    </Row>
                  )}
                </EditableContext.Consumer>
              </span>
            ) : (
              <Fragment>
                <Row>
                  <Col span={10}>
                    <Button
                      type="danger"
                      size="small"
                      style={{ width: "60px" }}
                      onClick={() => this.handleDelete(record.key)}
                    >
                      Delete
                    </Button>
                  </Col>
                  <Col span={8}>
                    <Button
                      type="primary"
                      size="small"
                      style={{ width: "60px" }}
                      disabled={editingKey !== ""}
                      onClick={() => this.edit(record.key)}
                    >
                      Edit
                    </Button>
                  </Col>
                </Row>
              </Fragment>
            );
          }
        }
      ],

      dataSource: [],
      key: 0,
      editingKey: "",
      name: "",
      price: "",
      type: "",
      age: "",
      address: "",
      phone: ""
    };
    this.namehandleChange = this.namehandleChange.bind(this);
    this.priceHandleChange = this.priceHandleChange.bind(this);
    this.typehandleChange = this.typehandleChange.bind(this);
    this.agehandleChange = this.agehandleChange.bind(this);
    this.addresshandleChange = this.addresshandleChange.bind(this);
    this.phoneHandleChange = this.phoneHandleChange.bind(this);
  }

  namehandleChange(event) {
    this.setState({
      name: event.target.value
    });
  }
  priceHandleChange(price) {
    this.setState({
      price: price
    });
  }

  typehandleChange(type) {
    this.setState({
      type: type
    });
  }
  agehandleChange(age) {
    this.setState({
      age: age
    });
  }
  addresshandleChange(event) {
    this.setState({
      address: event.target.value
    });
  }

  phoneHandleChange(phone) {
    this.setState({
      phone: phone
    });
  }

  isEditing = record => record.key === this.state.editingKey;

  cancel = () => {
    this.setState({ editingKey: "" });
  };
  edit(key) {
    this.setState({ editingKey: key });
  }

  handleDelete = key => {
    const dataSource = [...this.state.dataSource];
    this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
  };

  save(form, key) {
    form.validateFields((error, row) => {
      if (error) {
        return;
      }
      const newData = [...this.state.dataSource];
      const index = newData.findIndex(item => key === item.key);
      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row
        });
        this.setState({ dataSource: newData, editingKey: "" });
      } else {
        newData.push(row);
        this.setState({ dataSource: newData, editingKey: "" });
      }
    });
  }
  components = {
    header: {
      cell: ResizeableTitle
    },
    body: {
      cell: EditableCell
    }
  };
  handleAdd = () => {
    const {
      key,
      dataSource,
      name,
      price,
      type,
      age,
      address,
      phone
    } = this.state;
    const newData = {
      key: key,
      name: name,
      price: price + "$",
      type: type,
      age: age,
      address: address,
      phone: phone
    };
    if (
      name !== "" &&
      price !== null &&
      type !== "" &&
      age !== null &&
      address !== ""
    ) {
      this.setState({
        dataSource: [...dataSource, newData],
        key: key + 1
      });
    }
  };

  handleResize = index => (e, { size }) => {
    this.setState(({ columns }) => {
      const nextColumns = [...columns];
      nextColumns[index] = {
        ...nextColumns[index],
        width: size.width
      };
      return { columns: nextColumns };
    });
  };

  render() {
    const columns = this.state.columns.map((col, index) => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onHeaderCell: column => ({
          width: column.width,
          onResize: this.handleResize(index)
        }),
        onCell: record => ({
          record,
          inputType: col.dataIndex === "age" ? "number" : "text",
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record)
        })
      };
    });
    const { dataSource } = this.state;
    return (
      <Fragment>
        <Content style={{ margin: "2em 16px" }}>
          <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
            <Row gutter={20}>
              <Col span={3}>
                <Input
                  placeholder="Name"
                  value={this.state.name}
                  onChange={this.namehandleChange}
                  allowClear
                />
              </Col>
              <Col span={3}>
                <InputNumber
                  placeholder="Salary"
                  value={this.state.price}
                  onChange={this.priceHandleChange}
                  style={{ width: "150px" }}
                />
              </Col>
              <Col span={3}>
                <Select
                  onChange={this.typehandleChange}
                  defaultValue="Select"
                  style={{ width: "150px" }}
                >
                  <Option value="Waiter">Waiter</Option>
                  <Option value="Chef">Chef</Option>
                  <Option value="Employee">Employee</Option>
                </Select>
              </Col>
              <Col span={3}>
                <InputNumber
                  placeholder="age"
                  value={this.state.age}
                  onChange={this.agehandleChange}
                  style={{ width: "150px" }}
                  max={11}
                />
              </Col>
              <Col span={3}>
                <Input
                  placeholder="Address"
                  value={this.state.address}
                  onChange={this.addresshandleChange}
                  allowClear
                />
              </Col>
              <Col span={3}>
                <InputNumber
                  style={{ width: "150px" }}
                  placeholder="Phone"
                  value={this.state.phone}
                  onChange={this.phoneHandleChange}
                  max={11}
                />
              </Col>

              <Col span={3}>
                <Button
                  onClick={this.handleAdd}
                  type="primary"
                  style={{ marginBottom: 16, width: "100px" }}
                >
                  Add
                </Button>
              </Col>
            </Row>
            <EditableContext.Provider value={this.props.form}>
              <Table
                components={this.components}
                columns={columns}
                dataSource={dataSource}
                bordered
                rowClassName="editable-row"
                title={() => "Personal Information"}
                footer={() => "Footer"}
                pagination={false}
                scroll={{ y: 500 }}
              />
            </EditableContext.Provider>
          </div>
        </Content>
      </Fragment>
    );
  }
}

const WrappedHorizontalLoginForm = Form.create()(Personal);
export default WrappedHorizontalLoginForm;
