import React from "react";
import { Drawer, Row, Col, Input, InputNumber, Select, Button } from "antd";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange
}) => {
  return (
    <Drawer
      width={420}
      title="Basic Drawer"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Input
        placeholder="name"
        value={name}
        onChange={namehandleChange}
        allowClear
      />

      <InputNumber
        placeholder="price"
        value={price}
        onChange={priceHandleChange}
        style={{ width: "250px" }}
      />

      <Select
        onChange={typehandleChange}
        defaultValue="select type"
        style={{ width: "250px" }}
      >
        <Option value="snackðŸ¿">Snack</Option>
        <Option value="soupðŸœ">Soup</Option>
        <Option value="desertðŸ®">Desert</Option>
      </Select>

      <Button
        onClick={handleAdd}
        type="primary"
        style={{ marginBottom: 16, width: "100px" }}
      >
        Add
      </Button>
    </Drawer>
  );
};

export default Drawers;
