// React
import React from "react";

// Third part
import { Table, Button, Divider, Icon } from "antd";

const { Column } = Table;

const Tables = ({
  data,
  colOne,
  colTwo,
  colThird,
  colFourth,
  colFive,
  deleteItem
}) => {
  return (
    <Table bordered dataSource={data} rowKey={data => data.id}>
      <Column title={colOne} dataIndex="name" />
      <Column title={colTwo} dataIndex="price" />
      <Column title={colThird} dataIndex="type" />
      <Column title={colFourth} dataIndex="date" />
      <Column
        key={data => data.id}
        title={colFive}
        render={(text, record) => (
          <span>
            <Icon
              type="delete"
              theme="filled"
              style={{ fontSize: "20px", color: "#e60000" }}
              onClick={() => deleteItem(record.id)}
            />
            <Divider type="vertical" />
            <Icon
              type="edit"
              theme="filled"
              style={{ fontSize: "20px", color: "#e60000" }}
              //   onClick={() => deleteItem(record.id)}
            />
          </span>
        )}
      />
    </Table>
  );
};

export default Tables;

