import React, { useState, useEffect, Fragment } from "react";
import _ from "lodash";
import {
  Layout,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Divider,
  Button,
  Typography,
  Select,
} from "antd";
import { getUser } from "../../../../services/userServices";
import { name } from "../../../../config.json";
import { openNotification } from "../../../custom";
import UserInfo from "./userInfo";

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 24 },
};
const Profile = () => {
  const [user, setUser] = useState([]);
  const [currentUser] = useState(localStorage.getItem(name));

  useEffect(() => {
    const fetchUser = async () => {
      try {
        await getUser()
          .then((res) => res.data)
          .then((data) => setUser(_.find(data, ["last_name", currentUser])));
      } catch (error) {
        openNotification("error", "Can't get Orders", "Check Connectin");
      }
    };
    fetchUser();
  }, [currentUser]);

  const handleUpdate = (info) => {
    console.log(info);
  };

  return (
    <Fragment>
      <UserInfo data={user} />
      <Layout.Content style={{ margin: "2em 16px" }}>
        <div
          style={{ background: "#fff", marginTop: 20, minHeight: 280 }}
          className="border border-dark rounded"
        >
          <Form {...layout} className="w-50 m-5" onFinish={handleUpdate}>
            <Form.Item label="First Name" name="first_name">
              <Input placeholder="Enter Your First Name" />
            </Form.Item>
            <Form.Item label="Last Name" name="last_name">
              <Input placeholder="Enter Your Last Name" />
            </Form.Item>
            <Form.Item label="Gender" name="gender">
              <Select className="w-100" placeholder="Choose your gender">
                <Select.Option value="Male">Male</Select.Option>
                <Select.Option value="Female">Female</Select.Option>
              </Select>
            </Form.Item>
            <Form.Item label="age" name="age">
              <InputNumber placeholder="Enter your age" min={0} max={60} />
            </Form.Item>
            <Form.Item>
              <Button htmlType="submit">Edit</Button>
            </Form.Item>
          </Form>
        </div>
      </Layout.Content>
    </Fragment>
  );
};

export default Profile;

