import React, { Component } from "react";

import { Icon, Row, Button } from "antd";

import OrderDrawer from "../../common/orderDrawer";
import ListCard from "../../common/listCard";
import "../../../assets/css/Order.css";
import CustomModal from "../../common/modal";

const OPTIONS = [" Apples ", " CheeseBurger ", " Bananas ", " FrenchFries "];

export default class Order extends Component {
  state = {
    data: [
      {
        id: 1,
        table_number: 1,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Alan",
        order: ["bananase"]
      },
      {
        id: 2,
        table_number: 2,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Mike",
        order: ["cherry ", " orange "]
      }
    ],
    item_data: {},
    visible: false,
    modal_visible: false,
    id: 0,
    table_number: "",
    url: "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
    name: "",
    order: []
  };

  showDrawer = () => {
    this.setState({ visible: true });
  };

  showModal = item => {
    const data = { ...item };
    this.setState({ modal_visible: true, item_data: data });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };

  numberChange = number => {
    this.setState({ table_number: number });
  };

  nameChange = event => {
    this.setState({ name: event.target.value });
  };

  onNameChange = e => {
    const data = this.state.item_data;
    data.name = e.target.value;
    this.setState({ item_data: data });
  };

  // onOrderChange = e => {
  //   const data = this.state.item_data;
  //   data.order = e.target.value;
  //   this.setState({ item_data: data });
  // };

  handleOk = e => {
    // console.log(this.state.item_data);
    // let new_data = [];
    // let { item_data, data } = this.state;
    // new_data = data;
    // new_data.map(item => {
    //   if (item.id === item_data.id) {
    //     item = item_data;
    //     return false;
    //   }
    // });
    // this.setState({ data: new_data, modal_visible: false });
  };

  orderSelect = value => {
    this.setState({
      order: value
    });
  };

  handleOrder = () => {
    const { id, data, table_number, url, name, order } = this.state;
    const newData = {
      id: id,
      table_number: table_number,
      url: url,
      name: name,
      order: order
    };
    if (table_number !== null && name !== "" && order !== "") {
      this.setState({
        data: [...data, newData],
        id: id + 1
      });
    }
  };

  handleDelete = dataId => {
    const data = this.state.data.filter(item => item.id !== dataId);
    this.setState({ data: data });
  };

  handleCancel = () => {
    this.setState({ modal_visible: false });
  };

  render() {
    const {
      data,
      order,
      item_data,
      visible,
      table_number,
      name,
      modal_visible
    } = this.state;
    const filteredOptions = OPTIONS.filter(o => !order.includes(o));
    return (
      <div
        style={{
          background: "#fff",
          padding: 24,
          minHeight: 680
        }}
      >
        <Row>
          <Button
            type="primary"
            style={{ width: "150px", float: "right" }}
            onClick={this.showDrawer}
          >
            <Icon type="plus" />
            New Order
          </Button>
        </Row>
        <OrderDrawer
          visible={visible}
          onClose={this.onClose}
          table_number={table_number}
          name={name}
          order={order}
          filteredOptions={filteredOptions}
          numberChange={this.numberChange}
          nameChange={this.nameChange}
          orderSelect={this.orderSelect}
          handleOrder={this.handleOrder}
        />
        <ListCard
          data={data}
          handleShowModal={this.showModal}
          handleDelete={this.handleDelete}
        />
        <CustomModal
          visible={modal_visible}
          data={item_data}
          onCancel={this.handleCancel}
          onSubmit={this.handleOk}
          onNameChange={this.onNameChange}
          onOrderChange={this.onOrderChange}
        />
      </div>
    );
  }
}
