import React from "react";
import {
  Drawer,
  Input,
  InputNumber,
  Select,
  Button,
  Form,
  Row,
  Col
} from "antd";
const { Option } = Select;

const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  typehandleChange,
  handleSubmit,
  form
}) => {
  const { getFieldDecorator } = form;
  return (
    <Drawer
      width={420}
      title="AddItem"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form onSubmit={handleSubmit}>
        <Form.Item>
          {getFieldDecorator("")}
          {/* <Input
            placeholder="name"
            value={name}
            onChange={namehandleChange}
            allowClear
          /> */}
        </Form.Item>
        <Form.Item>
          <InputNumber
            placeholder="price"
            value={price}
            onChange={priceHandleChange}
            style={{ width: "370px" }}
          />
        </Form.Item>

        <Form.Item>
          <Select onChange={typehandleChange} defaultValue="select type">
            <Option value="snack🍿">Snack</Option>
            <Option value="soup🍜">Soup</Option>
            <Option value="desert🍮">Desert</Option>
          </Select>
        </Form.Item>
        <Row>
          <Col span={8}>
            <Form.Item>
              <Button htmlType="submit" type="primary">
                Add
              </Button>
            </Form.Item>
          </Col>
          <Col span={8}>
            <Form.Item>
              <Button onClick={onClose} type="danger">
                Cancel
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};
const WrappedRegistrationForm = Form.create({ name: "register" })(Drawers);
export default WrappedRegistrationForm;
