import React from "react";
import { Drawer, Row, Col, Input, InputNumber, Select, Button } from "antd";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange,
  ...rest
}) => {
  return (
    <Drawer
      width={620}
      title="Basic Drawer"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Row gutter={25}>
        <Col span={6} offset={2}>
          <Input
            placeholder="name"
            value={name}
            onChange={namehandleChange}
            allowClear
          />
        </Col>
        <Col span={5}>
          <InputNumber
            placeholder="price"
            value={price}
            onChange={priceHandleChange}
            style={{ width: "250px" }}
          />
        </Col>
        <Col span={5}>
          <Select
            onChange={typehandleChange}
            defaultValue="select type"
            style={{ width: "250px" }}
          >
            <Option value="snackðŸ¿">Snack</Option>
            <Option value="soupðŸœ">Soup</Option>
            <Option value="desertðŸ®">Desert</Option>
          </Select>
        </Col>
        <Col span={6}>
          <Button
            onClick={handleAdd}
            type="primary"
            style={{ marginBottom: 16, width: "100px" }}
          >
            Add
          </Button>
        </Col>
      </Row>
    </Drawer>
  );
};

export default Drawers;
