import React, { Fragment, useState } from "react";

import { Card, Avatar, Button, Input } from "antd";

const { Meta } = Card;

export default function Edit(props) {
  const [name] = useState("");
  const [order] = useState([]);

  //   props.location.state.name
  //   props.location.state.order

  nameChange = value => {
    name: value;
  };

  return (
    <Fragment>
      <Card
        title={props.location.state.title}
        cover={<img alt="" src={props.location.state.url} />}
        style={{ width: "400px" }}
      >
        <Meta
          title={<Input value={name} onChange={this.nameChange} />}
          description={<Input value={order} />}
        />
      </Card>
      <Button
        style={{ width: "150px", margin: "1em 0" }}
        onClick={() => props.history.push("/order")}
      >
        Back to Order
      </Button>
    </Fragment>
  );
}
